mapper.select:
SELECT log_id AS logId, admin_log.admin_id AS adminId, admins.admin_username AS adminUsername, target_type AS targetType, target_id AS targetId, action_type AS actionType, reason AS reason, metadata_json AS metadataJson, request_ip AS requestIp, user_agent AS userAgent, admin_log.created_at AS createdAt FROM admin_log LEFT JOIN admins ON admin_log.admin_id = admins.admin_id WHERE 1=1
AND admin_log.created_at >= #{start}
AND admin_log.created_at < #{end}
AND action_type = #{actionType}
AND target_type = #{targetType}
AND target_id = #{targetIdExact}
AND ( reason LIKE CONCAT('%', #{keyword}, '%') OR target_type LIKE CONCAT('%', #{keyword}, '%') OR CAST(admin_id AS CHAR) LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY admin_log.created_at DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM admin_log
WHERE 1=1

    AND created_at >= #{start}


    AND created_at < #{end}


    AND action_type = #{actionType}


    AND target_type = #{targetType}


    AND target_id = #{targetIdExact}


    AND (
        reason LIKE CONCAT('%', #{keyword}, '%')
        OR target_type LIKE CONCAT('%', #{keyword}, '%')
        OR CAST(admin_id AS CHAR) LIKE CONCAT('%', #{keyword}, '%')
    )
