mapper.select:
SELECT r.review_id AS reviewId, r.accommodations_id AS accommodationId, a.accommodations_name AS accommodationName, r.rating, r.content, COALESCE(r.author_name, u.nickname, u.name, u.email) AS authorName, r.visit_date AS visitDate, r.created_at AS createdAt, r.is_crawled AS isCrawled, rr.content AS replyContent, COALESCE(rr.updated_at, rr.created_at) AS replyUpdatedAt FROM review r JOIN accommodation a ON a.accommodations_id = r.accommodations_id JOIN users u ON u.user_id = r.user_id LEFT JOIN review_replies rr ON rr.review_id = r.review_id WHERE a.user_id = #{hostId} AND r.is_deleted = 0
AND r.accommodations_id = #{accommodationId}
ORDER BY r.created_at ASC
ORDER BY r.created_at DESC
LIMIT #{size} OFFSET #{offset}
