INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (10, '서귀포 삼달가옥감성숙소', 'GUESTHOUSE', '안녕하세요! [삼달가옥 감성숙소] 입니다~\n\n통창 넘어로 계절이 흐르는 ,조용한 정원의 숙소\n혼자쉬기좋은, 소음없이 완전한 쉼이 허락되는곳\n아이들과가기 좋은 제주\n\n저희 삼달가옥감성숙소 드라마 촬영지로 유명한\n 삼달리 바닷가 작은 마을에\n위치한 70년 세월을 견뎌낸 돌집으로\n만들어졌고, 오래도록 동네 이장님댁이였던\n구옥을 날것 그대로의 정겨움과 현대적인\n시설이 어우러진 곳입니다 :)\n\n싱그러운 제주 바람과 함께 맑고 깨끗한\n바다를 가까운 곳에서 만나보실 수 있어요.\n언제나 저희 숙소에 방문해주시는 분들을\n위해서 완벽한 컨디션을 유지 관리하고있습니다.\n\n친구, 가족, 연인과 함께 새소리와\n햇살 가득한 정원에서 오롯이 쉼이\n될 수 있는 힐링스테이를 느껴보세요!\n\n많은 이용 부탁드립니다~ 감사합니다.\n\n::이용안내::\n-입/퇴 - 16:00/11:00\n-기준인원 초과시 1인당 2만원\n-연박예약시 1회 조식제공\n-자쿠지는 본채에만 해당됩니다.\n-바베큐장 (숯은 제공x)\n\n::주변 관광지::\n아쿠아플라넷/섭지코지/성산일출봉\n표선해수욕장/드르쿰다 in 성산\n신천목장도보가능/일출명소\n이호해수욕장/낚시스팟\n월컴투삼달리촬영지\n해녀스팟', '제주', '서귀포시', '삼달하동로17번길 9', 33.3689471, 126.8705524, 'GS25 성산삼달점에서 도보 5분거리에 위치하고 있습니다.', NULL, NULL, null, 67, 'https://map.naver.com/p/entry/place/1427260807?c=15.00,0,0,0,dh&placePath=/room?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181206&locale=ko&svcName=map_pcv5&businessCategory=pension&fromPanelNum=1&additionalHeight=76&timestamp=202512181206&locale=ko&svcName=map_pcv5&businessCategory=pension&from=map', 'NAVER', 'Instagram: https://www.instagram.com/samdal_gaok/', NULL, NULL, '정겨운 분위기와 가까운 바다의 매력');
