INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (13, '제주 린든 게스트하우스', 'GUESTHOUSE', '[공지사항]\n예약 전 상담은\n가카오 오픈채팅 /제주린든/ 검색\n단톡방에 오시면 9명에 스텝들이\n게스트님에게 도움 드릴 것입니다\n\n린든 라운지에서\n85인치 티비로 즐기는 오늘의영화 보며 파티!\n21시 오늘의 영화 상영시 각종꽁자안주 서비스\n쉽고 편리한 무료 여행사진 인화 서비스\n컴퓨터, 플레이스테이션 무료 사용\n각종 무료 서비스 제공 린든 셀프 스토어 등\n다양한 서비스 와 이벤트 진행하고 있습니다.\n\n게스트님께서 꼼꼼히 챙겨 수준 높은 린든사용 가성비를 경험하시기 바랍니다.\n\n2인 도미토리 이상은 객실내 개별 화장실 및\n50인치 스마트TV로 무료넷플릭스 시청 가능\n\n체크인 17:00\n체크아웃 13:00\n\n제주공항에서 직통차량 10분거리에 위치\n숙소 바로 앞에 버스정류장이 있어 어디든 이동 가능합니다.\n특히 한라산 직통 버스 있습니다.\n\n지하에 기계 주차시설 20대 보유중 입니다.\n승용차/소형차는 언제든 주차가능 합니다.\n(suv 차량은 외부공용 주차장 이용)\n주차편의를 위해 오픈채팅 하시면\n린든 스텝이 주차 안내를 도와드립니다.', '제주', '제주시', '278 줌타워 6', 33.4999643, 126.5261883, '공항에서 버스 365번/370번/315번/43-1번\n타고 제주시청에 내려서 200미터 반경 내\n탐라 장애인 복지관 앞 6,7층 린든 호스텔 있습니다.\n\n제주 공항 및 터미널에서 5분거리에 있습니다.\n동문 야시장, 대학로 거리, 탑동 횟집 거리\n시청 대학로 번화가 바로 옆 입니다.\n\n한라산 성판악 등반시 281번 버스 정류장\n바로 앞에 있습니다.\n\n편의점 시장 유명식당이 주변에 산재한\n번화가 입니다.', '주차가능', '064-722-0419', null, 112, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EB%A6%B0%EB%93%A0+%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4/place/1937798504?c=8.00,0,0,0,dh&placePath=/room?entry=pll&fromPanelNum=2&timestamp=202512181207&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%A6%B0%EB%93%A0%2B%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4&businessCategory=guesthouse&fromPanelNum=2&timestamp=202512181207&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%A6%B0%EB%93%A0%2B%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4&businessCategory=guesthouse&from=map', 'NAVER', 'Instagram: https://www.instagram.com/lyndo_n_', '17:00', '13:00', '편리한 위치와 다양한 무료 서비스로 편안한 여행');
