INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (15, '제주 한라게스트하우스', 'GUESTHOUSE', '한라산 오름 올레길 등반 전문 한라게스트하우스입니다\n\n공항에서 버스 365번/370번/315번/43-1번\n타고 제주시청에 내려서 200미터 반경 내\n탐라 장애인 복지관 앞에 한라 게스트 하우스 있습니다.\n\n제주 공항 및 터미널에서 5분거리에 있습니다.\n동문 야시장, 대학로 거리, 탑동 횟집 거리\n시청 대학로 번화가 바로 옆 입니다.\n\n한라산 성판악 등반시 281번 버스 정류장이\n바로 앞에 있습니다.\n\n편의점 시장 유명식당이 주변에 산재한\n번화가 입니다.\n\n한라 라운지에서\n85인치 티비로 즐기는\n[오늘의영화 보며 포틀럭 파티]\n21시 오늘의 영화 상영시 각종꽁자안주 서비스\n쉽고 편리한 무료 여행사진 인화 서비스\n컴퓨터, 플레이스테이션 무료 사용\n각종 무료 서비스 제공 한라 셀프 스토어 등\n다양한 서비스 와 이벤트 진행하고 있습니다.\n\n2인 도미토리 이상은 객실내 개별 화장실 및\n50인치 스마트TV로 무료넷플릭스 시청 가능\n\n체크인 17:00\n체크아웃 13:00\n\n지하에 기계 주차시설 20대 보유중 입니다.\n승용차/소형차는 언제든 주차가능 합니다.\n(suv 차량은 외부공용 주차장 이용)\n전화주시면 셀프주차 안내를 도와드립니다.', '제주', '제주시', '278 7층', 33.4999815, 126.5262119, '탐라장애인복지관 버즈정류장 옆 줌타워건물 7층입니다', '주차가능', '0507-1414-1390', null, 20, 'https://map.naver.com/p/entry/place/1758932327?c=15.00,0,0,0,dh&placePath=/room?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181208&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&fromPanelNum=1&additionalHeight=76&timestamp=202512181208&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&from=map', 'NAVER', NULL, '17:00', '13:00', '좋은 접근성과 저렴한 숙박으로 편리한 선택');
