INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (22, '제주 배배게스트하우스', 'GUESTHOUSE', '모든 객실은 개인실 입니다.\n아직 제주의 정취가 남아있는 조용한 동네 신창 해안 도로 옆에 위치하고 있습니다. 도시의 화려함과 번잡함에서 벗어나 나 홀로 생각을 정리하고, 조용히 독서도 할 수 있으며 ,가족,연인,친구들과 많은 얘기를 나눌 수 있는 공간입니다. 신창항 낚시 배 타는 곳까지 걸어서 1~2분 거리, 신창 해안 도로는 걸어서 2분 거리입니다.\n간단히 제공 되는 조식은 직접 공용 주방에서 해서 드시면 됩니다.\n냄새, 연기가 많이 나는 음식 및 생선 손질은 주방에서 조리 하실 수 없습니다.', '제주', '제주시', '신창1길 20', 33.3472925, 126.1809007, '*공항에서 버스 탑승\n공항 4번 출구>>4번 정류장>>102번 버스>>한경면 사무소 하차>>버스 진행 방향으로 이동\n\n*자동차 이용\n1. 신창리 다목적 회관(오른쪽) 보이면, 직진 50m, 쓰레기 분리 수거함(왼쪽) 지나서, 돌담 끝나는 곳에서(펜더레저 간판 앞) 좌회전>>숙소 바로 보임', '주차가능\n무료', '0507-1412-8259', null, 46, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EB%B0%B0%EB%B0%B0%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4/place/38233085?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/room?fromPanelNum=1&additionalHeight=76&timestamp=202512181211&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%B0%B0%EB%B0%B0%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4&businessCategory=guesthouse&additionalHeight=76&timestamp=202512181211&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%B0%B0%EB%B0%B0%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4&businessCategory=guesthouse&fromPanelNum=1', 'NAVER', 'Blog: https://blog.naver.com/korasi', '16:00', '10:30', '깔끔한 방에서 즐기는 멋진 뷰');
