INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (26, '제주 블랑 게스트하우스', 'GUESTHOUSE', '에머럴드빛 표선해비치 해변과 가까운 블랑게스트하우스는 깨끗하고 편안한 실내와 침구, 조용한 주변환경, 맛있는 아침식사, 해비치해변 무료 픽업서비스 제공으로 제주를 여행하시는 모든 게스트 여러분의 즐거운 여행을 위한 안전한 쉼터가 되도록 서비스 하겠습니다.', '제주', '서귀포시', '표선관정로43번길 76-31', 33.3332157, 126.8269052, '>> 자가용\n제주공항에서 번영로를 타고 표선을 목표로 50분거리에 표선교차로에서 우회전 서귀포방면으로 200m에 있습니다.\n네비에 '''' 제주도 서귀포시 표선면 표선관정로43번길 76-31'''' 입력하세요\n>> 버 스\n제주공항에서 표선행 121번, 122번, 제주시외버스터미널에서 221번,222번 버스를 타시면 약 1시간 정도 소요되며 표선면사무소에서 하차하여 픽업요청 전화주세요. 바로 모시러 5분내에 도착합니다.\n>> 뚜벅이\n올레길을 걸으시거나 대중교통을 이용하시는 분들은 표선시내나 올레길 3코스 끝나는 지점 올레길 4코스 시작하는 지점 아니면 표선인근에서 어디든 언제든 픽업요청 전화주세요. 10분내에 모시러 갑니다.', '주차가능\n무료', NULL, null, 284, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EB%B8%94%EB%9E%91+%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4/place/36893522?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/room?fromPanelNum=1&additionalHeight=76&timestamp=202512181213&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%B8%94%EB%9E%91%2B%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4&businessCategory=guesthouse&additionalHeight=76&timestamp=202512181213&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%B8%94%EB%9E%91%2B%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4&businessCategory=guesthouse&fromPanelNum=1', 'NAVER', 'Blog: http://blog.naver.com/blancinjeju\nBlog: https://blog.naver.com/blancinjeju', '16:00', '10:00', '따뜻한 서비스와 맛있는 조식으로 하루를 시작');
