INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (40, '캡틴제주 게스트하우스', 'GUESTHOUSE', '캡틴제주 게스트하우스는 올레 18코스가 있는 조천읍 신촌리에 위치해 있으며 공항에서 차량 20분, 삼양해수욕장과 함덕해수욕장이 차량 5분 거리에 위치해 있으며, 버스정류장이 가까이 있어서 뚜벅이 여행자들도 편안히 제주여행을 하실 수 있습니다.\n\n또한, 4인 이상 모였을 시에는 간단하게 파티를 진행하며 여름에는 밤 낚시를 가기도 합니다. 사전예약자에 한해서 스냅사진촬영 투어도 진행하고 있으니 제주도 여행의 잊지 못할 추억을 만드실 수 있을 것 입니다.\n\n옥상과 테라스에서는 제주바다와 신촌포구, 농가주택들이 어우러져 있는 풍경을 바라 볼 수 있으며 일찍 일어나신 분들은 일출까지 감상할 수 있습니다. 주방에서는 취사가 가능하여 조식을 자유롭게 드실 수 있으며 식사 후에는 편안하게 쉬시다가 자유롭게 퇴실 하시면 됩니다.\n\n제주도 동쪽 여행의 시작점과 마지막점!!\n캡틴제주 게스트하우스로 여러분들을 초대합니다.', '제주', '제주시', '신촌3길 37', null, null, '- 제주공항 -\n325번 - 신촌초등학교 하차\n\n* 하차 후 신촌초등학교 옆 도로를 따라 5분정도 직진하시면 됩니다.', NULL, '0507-1405-7640', null, 19, 'https://map.naver.com/p/search/%EC%BA%A1%ED%8B%B4%EC%A0%9C%EC%A3%BC+%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4/place/36792362?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/room?fromPanelNum=1&additionalHeight=76&timestamp=202512181220&locale=ko&svcName=map_pcv5&searchText=%EC%BA%A1%ED%8B%B4%EC%A0%9C%EC%A3%BC%2B%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4&businessCategory=guesthouse&additionalHeight=76&timestamp=202512181220&locale=ko&svcName=map_pcv5&searchText=%EC%BA%A1%ED%8B%B4%EC%A0%9C%EC%A3%BC%2B%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4&businessCategory=guesthouse&fromPanelNum=1', 'NAVER', 'Blog: https://blog.naver.com/kdg890208', NULL, NULL, NULL);
