INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (49, '미르 게스트하우스 제주점', 'GUESTHOUSE', '제주 게하 탑 클래스 미르,\n유명 건축가 문훈 (Moon Hoon)의 작품에서 하룻밤 묵는 멋진 경험을 해보세요. lol\n제주여행의 의미와 쉼과 편안한 휴식처를 원하시는 분께 추천드립니다. 제주여행의 시작과 끝은 깔끔하고 느낌있는 프리미엄 게하 미르에서 하세요.\n\n유럽에 지점이 있는 글로벌 숙소!\n미르를 찾아주세요!\n\n최근 건물 전체의 리모델링을 통해 새롭게 거듭 태어나고 있습니다.\n에어비앤비 형태의 미르스테이 (7-8인) 독채펜션과 미르테라스 (4인) 도 새롭게 오픈했습니다!\n다양한 룸이 여러분의 모든 구성원에게 적합한 숙소가 될 것입니다.\n\n미르 게스트 하우스는 공항에서 택시 이용시 7분, 버스 이용시 15분 가량 소요 됩니다. (택시 이용시 5000-6000원정도라 짐이 많으신 분들은 택시를 더 추천드려요!)\n\n제주여행 어디를 가시던지 편하게 출발가능한 위치입니다.\n숙소 앞 1분거리에 버스 정류 장이 있어서 어디든 대중 교통을 이용하여 동문시장 및 산굼부리, 제주시청, 노형 등 대중 교통 노선을 이용하실 수 있습니다.\n\n미르에서 할 수 있는 일\n1. 옥상 용머리 전망대에서 여유로운 시간 보내기\n2. 아침에 용두암 해안도로로 조깅하기\n3. 미르 중정에서 하늘보며 해먹타기\n4. 미르 2층 게하카페에서 동문시장에서 사온 회먹기\n5. 옥상 야외 영화관에서 영화보기\n6. 미르 1층 야외 테라스에서 흡연하기\n\n미르는 단순한 게하가 아닙니다. 건물 디자인 설계부터 오롯이 게스트를 위한 공간으로 만들어진 곳 입니다.\n아직 이용 안하셨다면 지금 신청하세요!!', '제주', '제주시', '4', 33.5128344, 126.5106237, '제주공항에서 차로 5분 이내 거리 !!\n용두암 근접하여 산책과 해안도로 조깅코스는 정말 매력적입니다. 제주여행의 첫날과 마지막날은 미르가 딱 좋습니다. ^^', '주차가능', '0507-1445-2270', null, 1045, 'https://map.naver.com/p/search/%EB%AF%B8%EB%A5%B4+%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4+%EC%A0%9C%EC%A3%BC%EC%A0%90/place/35816106?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/room?fromPanelNum=1&additionalHeight=76&timestamp=202512181223&locale=ko&svcName=map_pcv5&searchText=%EB%AF%B8%EB%A5%B4%2B%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4%2B%EC%A0%9C%EC%A3%BC%EC%A0%90&businessCategory=guesthouse&additionalHeight=76&timestamp=202512181223&locale=ko&svcName=map_pcv5&searchText=%EB%AF%B8%EB%A5%B4%2B%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4%2B%EC%A0%9C%EC%A3%BC%EC%A0%90&businessCategory=guesthouse&fromPanelNum=1', 'NAVER', 'Instagram: http://www.instagram.com/mirguesthouse', '16:00', '10:00', '아름다운 건축 디자인의 좋은 분위기');
