INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (55, '제주 모이라 무인텔', 'GUESTHOUSE', '제주 애월 가성비 숙소, 모이라 무인텔\n\n제주시 애월읍에 위치한 모이라무인텔은 합리적인 가격과 깔끔한 객실, 다양한 편의시설을 갖춘 제주 가성비 숙박시설입니다. 제주공항에서 차량으로 약 20분 거리로 가까워 비행기 도착 직후나 출발 전 1박 숙소로도 이용이 편리합니다.\n무인 체크인 시스템으로 프런트 없이 빠르고 간편하게 입실 가능하며, 1인 여행자부터 커플, 출장 고객까지 누구나 편안하게 머물 수 있도록 구성되어 있습니다.\n\n-전 객실 스파 욕조 및 안마의자 구비\n-초고속 와이파이 & 고사양 PC, VOD 무료 시청\n-무료 생수, 캔음료, 커피 제공\n-전용 주차공간 완비 & 비대면 입실 가능\n\n제주공항 근처 숙소, 애월 숙소 찾고 계신다면,\n조용하고 쾌적한 프라이빗 무인텔 모이라에서 편안한 하루를 보내세요.\n\n제주시 애월읍 광령평화8길 20\n064-744-4463\n[네이버 예약 가능]', '제주', '제주시', '광령평화8길 20 모이라 무인텔', 33.4500348, 126.4406542, '제주공항에서 자차로 19분거리 (9.9km)', '주차가능', '0507-1341-4463', null, 12, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EB%AA%A8%EC%9D%B4%EB%9D%BC+%EB%AC%B4%EC%9D%B8%ED%85%94/place/1750601109?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/room?fromPanelNum=1&additionalHeight=76&timestamp=202512181226&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%AA%A8%EC%9D%B4%EB%9D%BC%2B%EB%AC%B4%EC%9D%B8%ED%85%94&businessCategory=motel&additionalHeight=76&timestamp=202512181226&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%AA%A8%EC%9D%B4%EB%9D%BC%2B%EB%AC%B4%EC%9D%B8%ED%85%94&businessCategory=motel&fromPanelNum=1', 'NAVER', NULL, NULL, NULL, NULL);
