INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (57, '제주 휴스테이 금호', 'GUESTHOUSE', '세계자연유산인 성산일출봉과 60m 거리에 위치하고 있으며, 특히 공항버스(111번,101번) 정류장과는 95m 정도 거리에 있습니다.\n\n성산일출봉, 오조해안, 우도, 광치기 해변을 도보로 산책할 수 있는 휴스테이금호에서 제주도 동부 여행을 계획해 보세요.\n\n전망 좋은 옥상에서 성산일출봉을 마주할 수 있고 시원한 바람을 맞으며 바라보는 바다일몰과 멀리보이는 한라산 또한 잊지 못할 추억을 남겨줄 것입니다.\n\n더블베드 객실타입의 호텔형 객실과\n펜션(더블베드 2개+싱글베드 2개+주방, 거실, 욕실, 세탁실)형 객실이 있습니다\n\n제주자치도에서 실시하는 숙소 위생평가에서 최우수업소로 지정되었습니다. 그만큼 객실의 청결, 위생을 중요시하며 편안함을 제공하려고 노력하고 있습니다.\n\n제주도 성산읍 성산중앙로30-11\n\n예약 및 소개 : http://hueStay.kr\n숙소 문의 : huestay_geumho@naver.com', '제주', '서귀포시', '성산중앙로 30-11 휴스테이금호', 33.4622458, 126.9338876, '공항1번게이트에서 30분 간격으로 출발하는 버스(111,112번 외)가 성산일출봉 버스정류장에서 95m거리에 있습니다.\n그외 제주 전역으로 이동하는 급행버스와 성산포항이 인접해있습니다.\n숙박 근처에 일출봉을 관람하거나 광치기해변, 오조로, 섭지코지 외 우도로 여행하기가 좋습니다.', '주차가능', '0507-1427-0069', null, 130, 'https://map.naver.com/p/entry/place/1145852247?c=15.00,0,0,0,dh&placePath=/information?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181227&locale=ko&svcName=map_pcv5&businessCategory=hotel&from=map&fromPanelNum=1&additionalHeight=76&timestamp=202512181227&locale=ko&svcName=map_pcv5&businessCategory=hotel', 'NAVER', 'Blog: https://blog.naver.com/leon3141', NULL, NULL, '깨끗한 방에서 먹는 맛있는 밥');
