INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (68, '제주 표리네', 'GUESTHOUSE', '안녕하세요:)\n제주 표선에 읍내권 중심에 있는 횰이 언니 눈매 0000.1초 닮은 쥔장\n표리네 게스트하우스입니다.\n\n저희 표리네는 (개별 욕실.개별 냉장고/ 1인실. 2인실.4인실,도미토리)룸 4개로 나뉘어져 편안한 휴식을 취하실수 있습니다.\n\n 제주의 힐링 여행 ( 올레길/ 해안길 자전거/ 바다의 서핑/대중교통)이용 하시는 분들에 편히가 잘 되어 있습니다.\n\n표리네는 조용히 쉬다가 가시는곳이라 파티가 없습니다.\n\n꼭 게스트하우스라 나이제한 어렵게 생각하시는데, 일반 숙박이라 생각하시면 더욱더 편안한 표리네 게스트하우스를 사랑하실꺼라 생각이 듭니다.\n당일 예약도 가능하니 언제나 문의 주세요 *^^*\n\n백사장이 이쁘기로 유명한 표선해수욕장도 도보로 5분거리에\n있어 여름에 물놀이 /모래놀이하기 좋습니다.\n\n주변 맛집 또한 도보 5분 거리에 있어 가볍게 다니시기에 좋은 ( 표리네 게스트하우스) 랍니다', '제주', '서귀포시', '표선중앙로 73-9 표리네게스트하우스', 33.3270994, 126.8330854, '표선면사무소, 표선환승정류장 맞은편 골목으로 들어오시면 돼요', NULL, '0507-1340-0286', null, 113, 'https://map.naver.com/p/entry/place/1446825452?c=15.00,0,0,0,dh&placePath=/room?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181232&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&fromPanelNum=1&additionalHeight=76&timestamp=202512181232&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&from=map', 'NAVER', NULL, '15:00', '11:00', '좋은 위치와 가성비를 자랑하는 편안한 숙소');
