INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (71, '제주 스테이 어라운드', 'GUESTHOUSE', '제주의 운치와 아름다움을 느낄 수 있는 곳, 스테이 어라운드를 소개합니다.\n\n바다의 푸른 물결이 부드럽게 밀려오는 도두봉 앞에 자리한 이 곳은 제주의 숨은 보석입니다. 바닷가를 따라 펼쳐진 무지개해안도로는 마치 자연이 그린 아름다운 수채화 같습니다. 아침이슬이 깃든 풀이 매듭처럼 감성을 자극하며, 도두봉 산책길은 마음의 여유를 선사합니다.\n\n바쁜 도심을 떠나 한적한 제주의 자연 속으로 빠져들어보세요. 스테이 어라운드에서는 고요한 제주의 풍경과 함께 힐링의 시간을 만끽할 수 있습니다. 여행의 피로를 풀어주는 따뜻한 숙소, 스테이 어라운드에서 당신을 기다립니다.', '제주', '제주시', '212', 33.5071563, 126.4699050, '외국어: 영어 짐보관: 가능 와이파이: 가능 기타 부대 시설 정보: 카페테리아(조리x, 취식가능) 주변 명소: 무지개 해안도로, 키세스존 주변 맛집: 순옥이...', '주차가능\n무료', '0507-1435-8943', null, 131, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EC%8A%A4%ED%85%8C%EC%9D%B4+%EC%96%B4%EB%9D%BC%EC%9A%B4%EB%93%9C/place/1875759726?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/room?fromPanelNum=1&additionalHeight=76&timestamp=202512181234&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EC%8A%A4%ED%85%8C%EC%9D%B4%2B%EC%96%B4%EB%9D%BC%EC%9A%B4%EB%93%9C&businessCategory=pension&additionalHeight=76&timestamp=202512181234&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EC%8A%A4%ED%85%8C%EC%9D%B4%2B%EC%96%B4%EB%9D%BC%EC%9A%B4%EB%93%9C&businessCategory=pension&fromPanelNum=1', 'NAVER', 'Instagram: https://www.instagram.com/stay_around_jeju', '16:00', '11:00', '아름다운 인테리어와 편리한 주변 시설');
