INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (72, '제주 고궁 펜션형 게스트하우스', 'GUESTHOUSE', '안녕하세요 제주도 성산포에 위치한 성산 고궁펜션형게스트.식당입니다.\n저희는 10월말에 오픈한 신축건물이며 몇없는 실제 제주도민이 운영하는곳입니다.\n1층은 식당 겸 휴게실 2~3층은 펜션4 게스트객실3 구조되어있으며\n1층식당에서는 숙박손님에 한하여 책,커피보면서 휴식을 취할수있으며\n원하시는분들은 주방에서 요리도 가능하십니다.\n아침은 다른곳과는 다르게 토스트나 죽이 아닌 간단한 한식뷔페로 무료로제공해드리며\n저녘은 바베큐파티장으로 쓰이며 그외 시간에는 제주도음식들은 판매하고있습니다.\n숙박손님들은 모든음식메뉴를 10%가격할인을 받으셔서 착한가격으로 제주도음식을 드실수있습니다\n2층은 남자 도미토리실 14인 , 4인(2층침대 2대)으로 구성된 펜션이 있습니다.\n3층은 여쟈 도미토리실 7인 방2개, 7인(2층침대 2대, 복층3인)으로 구성되있습니다.\n 오픈기념으로 3개월간 모든 숙박비는 20%가격인하해드리며, \n 100% 1인숙박권을 증정해드리는 이벤트를 하고있으니 많은관심부탁드립니다.\n 예약. 자세한설명과 사진은 블로그에서 확인가능하십니다.\n\n머물고 싶은 평안한 Healing Stay,\n자연의 아름다움이 가득한 고궁펜션형게스트에서 이용하시는 모든분들께서\n좋은 인연을 만들고 예쁜추억을 만들어 머무는 공간에 작은 이야기와 행복함이 가득하길 바라겠습니다. 감사합니다.', '제주', '서귀포시', '성산중앙로 54', null, null, '근처에 계시는데 찾아오시지 못하시는경우 연락을 주시면 상세히 알려드리겠습니다.\n근처 픽업도 가능합니다(바쁠때는.. 죄송해요 ㅠ)\n\n성산일출봉입구 버스정류장에서 내리셔서 주위를 둘러보시면 성산파출소가 눈에 보이십니다. 바로 맞은편입니다 ^^\n\n핸드폰으로 성산파출소 검색하시면 쉽게 찾아오실수있습니다. 감사합니다.', NULL, '064-782-7199', null, 81, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EA%B3%A0%EA%B6%81+%ED%8E%9C%EC%85%98%ED%98%95+%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4/place/37257067?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/room?fromPanelNum=1&additionalHeight=76&timestamp=202512181234&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EA%B3%A0%EA%B6%81%2B%ED%8E%9C%EC%85%98%ED%98%95%2B%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4&businessCategory=guesthouse&additionalHeight=76&timestamp=202512181234&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EA%B3%A0%EA%B6%81%2B%ED%8E%9C%EC%85%98%ED%98%95%2B%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4&businessCategory=guesthouse&from