INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (77, '제주 펄 호텔', 'GUESTHOUSE', '진주 보다 빛나는 행복을 전하는\n펄호텔제주\n\n연동 중심가에 위치하여 제주국제공항 4km(자동차 8분),\n신라, 롯데 면세점이 2km지점에 위치하고 있습니다\n제주의 아름다운 야경 이호테우 해변(5km)과\n제주도 원주민의 발상지인 삼성혈과 용연 계곡이 (6km) 있습니다.\n\n펄호텔 제주는 투어 데스크 안내를 통해 관광 일정을 예약하실 수 있습니다.\n그 밖에 게스트 라운지, 컨퍼런스룸,레스토랑을 편리하게 이용하실 수 있습니다.\n\n편안한 휴식처 객실은 에어컨, 주문형 영화 서비스, 냉장고,\n평면 TV, 헤어드라이기 등이 갖춰져 있습니다.\n호텔 내의 레스토랑에서 가족과 연인, 친구와 특별한 저녁을 보내실 수 있습니다\n\n1988년부터 오로지 고객님의 행복을 위해 호텔을 오픈한 그날부터 오늘에 이르기까지 진정성 있는 서비스를 위해 노력하고 있습니다\n감사합니다.', '제주', '제주시', '49', 33.4834697, 126.4909603, NULL, '주차가능', '064-742-8871', null, 180, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%ED%8E%84+%ED%98%B8%ED%85%94/place/13223452?c=15.00,0,0,0,dh&placePath=/information?entry=bmp&fromPanelNum=2&timestamp=202512181236&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%ED%8E%84%2B%ED%98%B8%ED%85%94&businessCategory=hotel&from=map&fromPanelNum=2&timestamp=202512181236&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%ED%8E%84%2B%ED%98%B8%ED%85%94&businessCategory=hotel', 'NAVER', NULL, '15:00', '11:00', NULL);
