INSERT INTO room (accommodations_id, room_name, price, min_guests, max_guests, room_description, main_image_url, bathroom_count, room_type, bed_count) VALUES (79, '트윈베드 커플룸', 135000, 2, 2, '일반객실, 기준 2인 (최대 2인) | 침실 1, 침대 2, 욕실 1, 최소 1박', 'https://search.pstatic.net/common/?autoRotate=true&quality=95&type=w750&src=https%3A%2F%2Fnaverbooking-phinf.pstatic.net%2F20180421_186%2F1524289356407LIyeL_PNG%2FIMG_9596-%25C6%25AE%25C0%25A9.png', 1, 'STANDARD', 2);
INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (80, '서귀포 모멘토 하우스', 'GUESTHOUSE', '제주서쪽 , 제주도동쪽 딱 서귀포 중간에 위치해 있어 어딜 이동해도 편리한 제주도한달살이 숙소로도 좋고, 제주도뚜벅이여행 으로도 좋고, 제주도숙소로 적합한 서귀포 가성비 숙소, 제주도관광에 전략적 요충지 최적의 베이스 캠프!!\n\n제주 여행은 모멘토와 함께 !!\n안녕하세요 모멘토하우스입니다 :)\n\n저희 게하(게스트하우스)는 서귀포 중심, 이중섭 거리 내에\n올레길 6,7코스가 만나는 지점에 위치해 있으며,\n\n먹거리가 풍부한 서귀포올레시장이 도보로 3분, 천지연폭포, 새연교\n제주여행추천하면 무조건 추천하는 관광지가 도보로 10분 !\n\n그리고 주변에 서귀포 맛집과 다양한 볼거리, 즐길 거리가 즐비해 있습니다.\n\n전 객실 무료Wi-Fi가 준비돼 있고, 여행객들이 편하게 휴식을 취하고 맛있는 음식을 먹을 수 있는 라운지와 공용 주방이 설치돼 있습니다.\n\n라운지에서는 무료조식과 저녁에는 제주게스트하우스 하면 빠질 수 없는 제주게스트하우스파티 중 하나인 포트럭 파티도 준비돼 있습니다.\n파티에 특징? 이 있다면 저희는 다른 게스트하우스와 달리 클럽 분위기 파티가 아닌 각 지역에서 온 여행자들과 함께 담소를 나누고 즐길 수 있는 차분한 분위기 파티입니다 :)\n\n또한, 저희 게하는 여러분에 특별하고, 기억남는 제주 여행을 위해\n제주도 대표 액티비티 스킨스쿠버 다이빙 샵 '' 야야다이브 ''와\n전기스쿠터 '' 제주올레길 바이크 오로오로 '' 까지 !\n\n모멘토하우스 이용하신 분들 모두 최대 20% 할인 !\n\n이 모든게 모멘토하우스 건물 내 상주하고 있어, 이용하는데 전혀 불편함이 없습니다 !\n\n단순히 머물다 가는 장소가 아닌, 여러분의 제주도여행을 더 기억 남도록 도와드리겠습니다 :)', '제주', '서귀포시', '21 모멘토하우스', 33.2465060, 126.5648764, '*공항 리무진 이용 : 600번 버스 승차->''샛기정 공원'' 정류장 하차 후 도보로 5분\n\n*렌트카 이용시 : ''모멘토 하우스'' 검색\n 주소 검색시 _ 서귀포시 태평로439번길 21\n 검색 후 오시면 됩니다 :)\n\n올레 시장 도보 3분\n이중섭거리 도보 1분\n올레길 6,7코스 인접\n천지연폭포/정방폭포/자구리공원 도보 10분\n성판악 입구까지 40분', '주차가능\n무료\n선착순으로 3대 가능 (만차시 주변 무료주차장 이용) 도미 토리 이용객은 건물 내 주차이용 제한적입니다._주변 무 료주차장 이용해주세요 ! (자전거/오토바이 제외) *입실 당일 날 안내문자를 보내드립니다 ! 주차관련 정보 ...', '0507-1315-2716', null, 93, 'https://map.naver.com/p/entry/place/1880715113?c=15.00,0,0,0,dh&placePath=/room?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181237&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&fromPanelNum=1&additionalHeight=76&timestamp=202512181237&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&from=map', 'NAVER', 'Instagram: https://www.instagram.com/momento_house_\nBlog: https://blog.naver.com/momento_house', '16:00', '11:00', '쾌적한 방에서 즐기는 맛있는 조식');
