INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (81, '제주 월정리 소낭스테이', 'GUESTHOUSE', '제주도 동쪽 예쁜 그림 같은\n월정리 먼 바다와 전원 마을이 보이는\n소낭 스테이 & 게스트하우스 입니다.\n소낭 스테이 와 소낭 게스트하우스는\n4층 건물 2층과 3층에서\n통합 운영하고 있습니다\n\n전 객실 월정리 먼 바다와\n전원 마을이 보이는\n테라스가 있는 오션뷰\n객실로 배정됩니다\n\n전 객실 넷플릭스, 유튜브 시청이\n가능한 엘지 룸 앤 스마트\n티브이와 블루투스 스피커\n\n전 객실 내 욕실 용품이 준비된 욕실\n\n건물 1층은 전용 주차장과\n엘리베이터가 있으며\n신속하고 편안한\n셀프 체크인 시스템으로 운영\n\n짐 보관은 소낭게스트하우스\n(1호점)에서\n편안하게 보관 가능\n\n쓩쓩 렌탈 월정점 이용 시 2천원\n현금 할인\n\n초밥공장 식사 시 음료수 제공\n\n월정리 서핑강습 희망 시\n할인 제공\n\n소낭스테이 환불규정\n\n숙박일로부터 8일 전 전액환불\n숙박일로부터 7일 이내 환불불가\n\n항공기 결항에 관한 환불 규정\n- 숙박일 당일 결항 편에 대해서 결항확인서를 보내주시면\n환불이 가능합니다\n- 숙박일 외의 결항확인서는 유효하지 않습니다', '제주', '제주시', '월정3길 7', null, null, '제주공항에서 소낭스테이 오는 방법\n\n제주 공항에서 101번 탑승\n월정리 해수욕장 하차\n세븐일레븐 월정점 바로 뒷 건물', '주차가능\n무료', '0507-1359-4540', null, 18, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EC%9B%94%EC%A0%95%EB%A6%AC+%EC%86%8C%EB%82%AD%EC%8A%A4%ED%85%8C%EC%9D%B4/place/607569797?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/room?fromPanelNum=1&additionalHeight=76&timestamp=202512181238&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EC%9B%94%EC%A0%95%EB%A6%AC%2B%EC%86%8C%EB%82%AD%EC%8A%A4%ED%85%8C%EC%9D%B4&businessCategory=pension&additionalHeight=76&timestamp=202512181238&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EC%9B%94%EC%A0%95%EB%A6%AC%2B%EC%86%8C%EB%82%AD%EC%8A%A4%ED%85%8C%EC%9D%B4&businessCategory=pension&fromPanelNum=1', 'NAVER', 'Blog: https://blog.naver.com/sonangstay', '16:00', '10:30', '깨끗한 내부에서 즐기는 아름다운 뷰');
