INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (82, '서귀포 풍경 호텔', 'GUESTHOUSE', '제주도 서귀포시에 위치한 휴양형 호텔입니다.\nCozy 스타일 호텔로 모던하고 깔끔한 객실 디자인과\n본관 전객실 아름다운 공원도시뷰 조망과 한라산이 보이는 마운틴 뷰 조망을\n별관 전객실 아름다운 오션뷰를 자랑합니다.\n\n진정한 힐링과 가족여행의 추억을\n서귀포 풍경호텔에서 만들어 보세요\n\n오랜 시간 고객님을 맞이하기 위해 분주히 준비하고, 저희 호텔을 만족해하실 고객님을 생각하며\n큰 희망에 찬 꿈을 꾸어 온 시간이 지나고 이제 꿈이 현실이 될 시간이 왔습니다.\n\n다양한 부대시설과 고객님들 한 분 한 분을 위한 특별한 이벤트, 차별화된 객실 서비스로 고객분들을 맞이하겠습니다.\n객실에 포함된 스킨스쿠버,승마, ATV같은 다양한 익스트림상품을 경험하면서 제주의 특별함을 즐겨 보세요.\n1키로거리의 서귀포 올레시장과 800미터거리의 아랑조을거리(먹자골목), 서귀포잠수함,서귀포유람선,새연교,외돌개,천지연폭포,이중섭거리등 근거리에 위치해있어서 다양한 먹거리와 볼거리즐길수 있습니다.', '제주', '서귀포시', '147 풍경호텔', 33.2447876, 126.5534616, '네이버 검색: 제주공항5번출구에서 600번공항리무진버스승차후 1시간30분정도소요(버스비용대략5500원),샛기정공원하차 후 걸어서500미터에 위치함', '주차가능', '0507-1342-3355', null, 86, 'https://map.naver.com/p/search/%EC%84%9C%EA%B7%80%ED%8F%AC+%ED%92%8D%EA%B2%BD+%ED%98%B8%ED%85%94/place/37421363?c=15.00,0,0,0,dh&placePath=/information?entry=bmp&fromPanelNum=2&timestamp=202512181238&locale=ko&svcName=map_pcv5&searchText=%EC%84%9C%EA%B7%80%ED%8F%AC%2B%ED%92%8D%EA%B2%BD%2B%ED%98%B8%ED%85%94&businessCategory=hotel&from=map&fromPanelNum=2&timestamp=202512181238&locale=ko&svcName=map_pcv5&searchText=%EC%84%9C%EA%B7%80%ED%8F%AC%2B%ED%92%8D%EA%B2%BD%2B%ED%98%B8%ED%85%94&businessCategory=hotel', 'NAVER', 'Blog: https://blog.naver.com/skjejudo', '15:00', '11:00', '조용하고 안락한 분위기의 호텔');
