INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (86, '제주 팀버하우스', 'GUESTHOUSE', '팀버하우스는 제주에서 가장 아름다운 곽지해수욕장 근처에 위치한 신축 펜션입니다.\n\n몸에 좋은 편백나무를 이용해 아늑하고도 편안한 공간을 마련해 두었습니다.\n\n근처엔 제주도 대표 관광지인 곽지해수욕장, 애월한담해변, 한림공원 등과 근접해 있어\n\n아름다운 풍경들을 제대로 감상할 수 있는 즐거운 제주 여행이 되실 거예요.\n\n펜션 지하에는 스크린 골프장이 마련되어 있어 전혀 지루하지 않은\n\n특별한 경험을 선사합니다. 사계절 이용 가능한 바베큐장에서 도란 도란 즐기며\n\n서로 좋은 추억을 쌓으시기를 바랍니다. 최고급 시설과 친절한 사장님이 항상\n\n고객님들을 맞이할 준비를 하고 있겠습니다. 감사합니다.\n\n제주도 애월,곽지 지역 펜션 숙박 찾으시는분들은 많은문의부탁드립니다.', '제주', '제주시', '곽지7길 11', 33.4509098, 126.3078687, '자가용 이용 시 ''곽지해수욕장'' 네비 검색 후 골목 진입하여 첫번째 골목에서 좌회전해서 100m 정도 들어오시면 됩니다.', NULL, '0507-1339-4241', null, 102, 'https://map.naver.com/p/entry/place/1684082833?c=15.00,0,0,0,dh&placePath=/room?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181240&locale=ko&svcName=map_pcv5&businessCategory=pension&fromPanelNum=1&additionalHeight=76&timestamp=202512181240&locale=ko&svcName=map_pcv5&businessCategory=pension&from=map', 'NAVER', 'Instagram: http://www.instagram.com/timber_house', '15:00', '11:00', '가까운 해변과 청결한 시설에서의 휴식');
