INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (92, '제주 수원성 독채민박', 'GUESTHOUSE', '수원성민박은 제주시 한림읍 마을 안에 위치한\n하루 한 팀만 이용하는 100평 규모 독채 숙소입니다.\n\n관광지 중심에 있는 숙소는 아니지만\n조용한 환경에서 프라이빗한 휴식을\n원하시는 분들께 적합한 공간입니다.\n\n가족 여행이나 차분한 단체 모임에 어울리며\n부모님을 동반한 숙박에도 많이 선택됩니다.\n\n집 내부 전체는 편백나무 인테리어로 구성되어\n머무는 동안 은은한 나무 향과 함께\n편안한 시간을 보내실 수 있습니다.\n\n[이용 정보]\n- 기준 인원 8명 최대 인원 15명\n- 초과 인원은 사전 유선 문의\n- 입실 14:00 이후\n- 퇴실 12:00\n- 방 3개\n- 주방 완비\n- 화장실 2개 실내 1 실외 1\n\n[공간 특징]\n- 잔디 마당이 있는 독채 숙소\n- 야외 바베큐 시설 무료 제공\n- 숯과 식재료는 직접 준비\n- 반려견 동반 가능\n- 반려견 용품은 직접 준비\n\n[주변 환경]\n- 도보 약 5분 거리 바닷가\n- 협재해수욕장과 한림공원 차로 약 10분\n- 차로 3분 거리 대형마트 위치\n\n조용한 숙소 특성상\n파티 목적 및 늦은 밤 소음이 있는 이용은\n정중히 사양합니다.\n\n한적한 제주 마을에서\n조용히 머무는 하루를 원하신다면\n수원성민박에서 편안한 시간을 보내세요.', '제주', '제주시', '수원7길 47-3', null, null, '담 따라서 안쪽으로 쭉 들어오세요 :D', '주차가능\n무료\n숙소 입구에 있는 공영 주차장 이용하셔도 됩니다.', '0507-1433-8837', null, 4, 'https://map.naver.com/p/entry/place/1436210558?c=15.00,0,0,0,dh&placePath=/room?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181243&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&fromPanelNum=1&additionalHeight=76&timestamp=202512181243&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&from=map', 'NAVER', 'Instagram: https://www.instagram.com/jeju_suwonseong/', NULL, NULL, NULL);
