INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (97, '함덕 535 독채민박', 'GUESTHOUSE', '제주 함덕해수욕장 도보 5분이내 위치한 함덕535독채민박입니다.\n\n숙소는 침대방1, 온돌방1, 욕실, 주방 겸 거실로 이루어진 공간이고\n작은 마당과 마당안에 주차 가능합니다.\n\n버스정류장, 마트, 편의점, 약국, 병원 각종 편의시설들 도보3분이내 위치해있고 함덕 맛집으로 유명한 고깃집들 도보로 가능합니다!!\n\n[이용수칙]\n체크인: 오후4시\n체크아웃: 오전11시\n* 숙소 정원은 최대4인까지 가능합니다.\n기준인원 외 인원추가 비용 1인 1일 1만원씩\n\n함덕 가지가지상점 뒷편에 민박집 있어요 : )', '제주', '제주시', '신북로 535-1', 33.5416592, 126.6670834, '- 제주공항에서 버스 이용시\n\n제주공항에서 빨강색 101번 버스 타셔서 9개 정거장 이동\n함덕환승정류장(함덕해수욕장)에서 내리셔서 파리바게트 보이는곳으로 쭉! 오세요~ 옆에 빨래방 그 옆에 식당 그 옆에 가지가지상점이 보이는데 같은 건물 뒷편에 함덕535민박이 있어요\n\n- 차로 오시는 길\n\n네이버에 신북로 535-1 또는 함덕535 검색!\n\n- 주차는 숙소 마당에 하시면 됩니다\n( 출입구가 좁아 주차 난이도가 조금 있습니다)', '주차가능\n무료\n숙소 입구 앞 마당에 주차 가능해요. 주차 어려우신 분들은 근처 공영주차장이나 골목, 스타벅스 주차장 이용해주세요', '0507-1422-9566', null, 8, 'https://map.naver.com/p/entry/place/1143588018?c=15.00,0,0,0,dh&placePath=/room?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181245&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&fromPanelNum=1&additionalHeight=76&timestamp=202512181245&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&from=map', 'NAVER', 'Blog: https://blog.naver.com/hamdeok_bubu\nInstagram: http://www.instagram.com/sangyoup_han_9566', NULL, NULL, NULL);
