INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (102, '제주 몬딱 게스트하우스', 'GUESTHOUSE', '<성인 19세 부터~ 39세 이하 >\n 동의 하신분만 예약 진행.\n운영 12년차 고민 끝에 결정되었습니다\n\n< 네이버예약시 할인 이벤트 >\n30,000-> 28,000 원\n\n<몬딱 감성 불멍 파티 >\n*주류는 각자 취향대로 즐길정도만 *\n밤새 마시고 달리실분은 다른곳을 이용해주세요\n\n1, 저녁 8시 시작 1-2시간 종료\n2, 오픈키친 마시카 23시30분까지 즐기기\n\n이후 음주,운전,외출은 안전을 위해 모두 금지.\n\n불멍과 오픈키친에서 분위기 있게 소소하게 이야기하며\n어울리는 자리입니다. 품격있고 매너있으신분만 오세요\n\n< 몬딱 >\n제주도 구좌읍 세화리에 위치한 몬딱 게스트하우스 입니다.\n몬딱은 제주어로 모두,몽땅,,모조리 란 뜻입니다.\n\n*가격정보*\n\n 1인 3만원\n\n새로운 업그레이드 불턱. 불멍과 자연에서 느끼는 휴식.\n텃밭체험.별보기 등등\n\n그날 게스트분들과 분위기에 따라 조금씩 다른 매일\n몬딱에서 즐거운 추억 만들어보아요', '제주', '제주시', '세화서길 7-31', 33.5192201, 126.8524596, '문자로 개별 연락드리고있습니다.\n큰 길에서 @ 다이소 @ 건물을 발견하실수있어요\n\n<네비>\n세화요양원 을 찍고 오셔서 몬딱 팻말을 보며 들어오시면되겠습니다.', '주차가능\n무료\n주차 블럭표지판을 세워둔 곳에 주차하시면 됩니다 .주차 공간이 여유가 많아 주차하실때 어려움은 없으실겁니다', NULL, null, 185, 'https://map.naver.com/p/entry/place/34646878?c=15.00,0,0,0,dh&placePath=/room?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181247&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&fromPanelNum=1&additionalHeight=76&timestamp=202512181247&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&from=map', 'NAVER', 'Blog: https://blog.naver.com/ssiren4\nInstagram: https://www.instagram.com/montak_guesthouse/', '17:00', '10:00', '친절한 서비스와 매력적인 게스트하우스의 만남');
