INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (107, '제주 그디글라 게스트하우스', 'GUESTHOUSE', '한적한 애월읍의 작은 시골마을 광령리에 위치해있습니다.\n올레길 16코스 종점 부근이라 올레길을 걷다가 만날수 있어요~\n넓은 잔디밭 앞마당에서는 야외에서 바베큐등을 하실수 있고요.\n애완동물 친화 가게라 모두모두 환영합니다.\n\n2층은 50평정도로 방은 총6개(욕실은 각방마다 있어요)\n거실겸 주방에서는 조리기구등이 있어서 요리를 하실수 있고,\n야외에 있는 넓은 주차장이 있어서 주차도 편리합니다.\n\n단독으로 예약시에는 약20명 정도( 미취약아동은 추가가능) 이며\n추가인원은 상담주세요.~\n\n올레길 걷는 분들도 환영합니다.\n모두모드 즐거운 여행 되시고, 문의사항은 연락주세요~', '제주', '제주시', '광령4길 10-6 그디글라 게스트하우스', 33.4589603, 126.4338849, '광령초등학교에서 도보5분거리 입니다.\n올레길 16코스 종점부근에서 5분거리에 위치해 있으며,\n건물옆에 넓은 야외 주차장이 있습니다.', '주차가능\n무료\n건물옆을 지나서 안쪽으로 쭉~ 들어오시면 넓은 야외 주차장이 있습니다.', '0507-1377-4113', null, 0, 'https://map.naver.com/p/entry/place/1228037693?c=15.00,0,0,0,dh&placePath=/information?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181250&locale=ko&svcName=map_pcv5&businessCategory=hotel&from=map&fromPanelNum=1&additionalHeight=76&timestamp=202512181250&locale=ko&svcName=map_pcv5&businessCategory=hotel', 'NAVER', 'Instagram: https://www.instagram.com/jeju_gdygla', '15:00', '10:30', NULL);
