INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (115, '제주 킴스캐빈 게스트하우스', 'GUESTHOUSE', '서귀포시 성산읍에 위치한 파티없는 조용한 숙소\n제주도 애견동반 가능한 펜션형 게스트하우스\n2인실/4인실/가족실\n\n제주올레1코스 안내소 옆\n제주시/서귀포시 시외버스 터미널에서\n201번 버스 타시고 시흥리 하차\n\n킴스캐빈은 버스정류장으로부터\n도보 10여분 거리에 위치합니다.\n눈.비 오거나 일몰 후 도착하시어\n전화주시면 버스정류장 픽업합니다.\n\n우도 배 타는 성산항과 성산일출봉이\n버스로 10분 거리에 위치하며\n주변에 섭지코지, 비자림, 만장굴 등\n다수의 관광지가 산재하여 있으며\n다랑쉬오름 용눈이오름 등이 가까이 있어\n오름투어에 좋은 숙소입니다', '제주', '서귀포시', '시흥상동로53번길 88-54', 33.4745041, 126.8866485, '제주공항에서 버스로 오실 경우\n공항에서 101/111번 버스를 타시고\n101번은 세화 환승정류장에서\n111번은 고성 환승정류장에서\n 201번을 갈아 타시고 시흥리에서 하차하시면 됩니다.\n저녁식사는 환승정류장 부근에서 하시는 것을 권합니다.', '주차가능', '0507-1408-5026', null, 111, 'https://map.naver.com/p/entry/place/33595825?c=15.00,0,0,0,dh&placePath=/room?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181253&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&fromPanelNum=1&additionalHeight=76&timestamp=202512181253&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&from=map', 'NAVER', NULL, '17:00', '10:00', NULL);
