INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (118, '제주 펜션정원 게스트하우스', 'GUESTHOUSE', '초가 정원의 첫 자랑은, 조용한 주변 산책 길이예요.\n옛 제주도 정의현의 도읍지였던 성읍민속마을 길은 고즈넉하고 고풍스럽습니다. 아침저녁으로 계절에 따라 들려오는 자연의 소리와 사랑하는 사람과 함께 걸으면 잊을 수 없는 추억이 쌓인답니다.\n전통 초가와 쾌적한 대청마루로 이루어진 초가 정원은, 실내는 현대식으로 구성되어 편리한 시설로 갖춰줘 있고 너른 잔디마당으로 반려견과 함께 독채로 사용하 실 수 있어서 복잡하고 불편했던 일상에서 벗어나 한동안 조용하고 건강하게 힐링 하 실 수 있으리라 생각합니다. 계획했던 일들에서, 스트레스 받았던 모든 것들에서, 잠시 내려놓으시고 가족 또는 커플, 친구 사랑하는 사람과 함께 소중한 시간 보내셨으면 좋겠네요.\n문의 사항은 에어비앤비나 카카오톡채널로 문의주세요 ^ ^\n\n(https://abnb.me/PD5VpL2Uhyb)\n(http://pf.kakao.com/_GxfAUxj)', '제주', '서귀포시', '성읍정의현로56번길 25 초가정원', null, null, '표선 성읍민속촌 정의골 동문으로 오시면 됩니다 ^^', '주차가능\n무료', '0507-1452-0543', null, 35, 'https://map.naver.com/p/entry/place/854530643?c=15.00,0,0,0,dh&placePath=/information?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181255&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&from=map&fromPanelNum=1&additionalHeight=76&timestamp=202512181255&locale=ko&svcName=map_pcv5&businessCategory=guesthouse', 'NAVER', 'Instagram: https://www.instagram.com/1220jjanga?igsh=bXczZDczeXUyYTYx', NULL, NULL, NULL);
