INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (119, '서귀포 알스테이', 'GUESTHOUSE', '우리집은 서울역, 종로 인사동, 제주시에 지점이 있는 게스트하우스입니다.\n한국을 찾는 외국여행객들에게 한국여행의 즐거움을 드리고자 합니다.\n아랑조울 먹자골목 입구, 올레시장, 서귀포시내에 있어서 밤늦게까지 서귀포 관광을 즐기기에 좋습니다. 공항 리무진버스 정류소가 걸어서 1분 거리에 있어서 공항으로 이동도 편리합니다.\n서귀포항까지 걸어서 5~10분거리로 가벼운 산책을 겸한 운동을 추천드립니다.', '제주', '서귀포시', '14 알 스테이', null, null, '제주공항에서 공항리무진버스(600번)를 타고 오시면 됩니다.\n서귀포시내(아랑조율거리 입구), 먹자골목과 올레시장까지 걸어서 1~3분 거리에 있고 이중섭거리, 서귀포항, 정방폭포, 세연교,...서귀포 시내 관광지가 걸어서 다 갈수 있는 곳에 있습니다.\n여유로운 제주, 서귀포 관광을 자동차 없이 즐겨보세요.', '주차가능', '0507-1324-5477', null, 0, 'https://map.naver.com/p/search/%EC%84%9C%EA%B7%80%ED%8F%AC+%EC%95%8C%EC%8A%A4%ED%85%8C%EC%9D%B4/place/1533118679?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/information?fromPanelNum=1&additionalHeight=76&timestamp=202512181256&locale=ko&svcName=map_pcv5&searchText=%EC%84%9C%EA%B7%80%ED%8F%AC%2B%EC%95%8C%EC%8A%A4%ED%85%8C%EC%9D%B4&businessCategory=motel&fromPanelNum=1&additionalHeight=76&timestamp=202512181256&locale=ko&svcName=map_pcv5&searchText=%EC%84%9C%EA%B7%80%ED%8F%AC%2B%EC%95%8C%EC%8A%A4%ED%85%8C%EC%9D%B4&businessCategory=motel', 'NAVER', NULL, NULL, NULL, NULL);
