INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (123, '제주 일출언덕신산 게스트하우스', 'GUESTHOUSE', '사람과 사람간의 소통을 기본운영방침으로 하여 운영하고 있는\n\n가족의 정다움을 느낄수 있는 게스트하우스입니다.\n\n제주도 동부권 올레길, 자전거하이킹, 오토바이여행, 각종관광지 여행, 자유 여행을 하는데 가장 편안한 숙소.\n\n2010년부터 오픈해서 운영하고 있고, 도미토리형 게스트하우스 입니다.\n\n2013년에 가족실을 새로 만들어 유아동반 가족들도 편안히 이용할수 있는 게스트하우스 입니다.\n\n제주도 해안길인 일주도로변에 있어서 올레1코스부터 6코스까지는 대중교통으로 30분안에 이동가능\n\n그리고 한라산 등반까지 모두 가능합니다.\n\n그리고 신산-신양해안도로와도 인접해 도보로 이용이 가능하며, 신산포구에서는 낚시도 가능합니다.\n\n해안가 언덕에 위치해 있어서 바로 바다가 보이며,\n\n아침엔 마당에서 또는 숙소 창문을 통해 일출을 볼수 있답니다.\n\n마을안에 위치해 있어 제주도의 실생활 모습을 보실수 있는 민박형 게스트하우스 이기도 합니다.\n\n많은 분들의 편안한 쉼터가 되기위해 열심히 만들어 나가겠습니다.', '제주', '서귀포시', '환해장성로121번길 21', 33.3842282, 126.8799820, '동일주버스(201번 버스) 신산리에 위치해 있는 난산입구정류장(기상청건물이 있음)에서 하차 도보로 5분안에 오실수 있습니다.', NULL, '064-782-1375', null, 1, 'https://map.naver.com/p/entry/place/13430862?c=15.00,0,0,0,dh&placePath=/information?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181258&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&from=map&fromPanelNum=1&additionalHeight=76&timestamp=202512181258&locale=ko&svcName=map_pcv5&businessCategory=guesthouse', 'NAVER', NULL, NULL, NULL, NULL);
