INSERT INTO accommodation_image (accommodations_id, image_url, image_type, sort_order) VALUES (123, 'https://search.pstatic.net/common/?autoRotate=true&type=w278_sharpen&src=https%3A%2F%2Fldb-phinf.pstatic.net%2F20150901_292%2F1441035556799GlUft_JPEG%2F13430862_4.jpg', 'detail', 4);
INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (124, '제주 포비네오두막 게스트하우스', 'GUESTHOUSE', '[ 월정리 바다와 함께하는 힐링 여행 ]\n\n안녕하세요~ 제주 월정리에 위치한\n포비네오두막 게스트하우스를\n찾아주셔서 감사드립니다 :)\n\n포비네오두막 게스트하우스는\n10년 이상 운영 중에 있는, 안심하고\n방문하실 수 있는 안전한 숙소입니다!\n\n제주 월정리 해수욕장 인근 바닷가\n어촌마을에 위치한 농가주택으로,\n여행자분들께 따뜻한 휴식의 공간과\n다양한 체험 프로그램을 제공합니다.\n\n<< ROOM 안내 >>\n*2인실A 50,000원(1인기준 25,000원)\n*2인실B 60,000원(1인기준 30,000원)\n*2인실C 60,000원(1인기준 30,000원)\n*여성 4인 도미토리-20,000원\n*남성 4인 도미토리-20,000원\n*서핑강습(서핑샵 연계상품)\n\n2인실과 4인실이 준비되어 있으며,\n월정리 해수욕장에서 진행되는\n서핑 강습이 포함된 서핑 캠프를\n함께 운영하고 있습니다.\n\n월정리 바다에서 즐기는 서핑은\n포비네오두막 게스트하우스의\n대표 프로그램으로, 전문 강사진이\n함께하는 서핑강습을 통해 초보자들도\n쉽게 서핑을 배워보실 수 있습니다!\n\n서핑 외에도 여행자 간 이야기를\n나눌 수 있는 소소한 파티도 진행하며,\n바베큐파티, 오름/숲길투어, 야간 물놀이 등\n다양한 체험 프로그램이 준비되어 있으니\n특별한 추억을 만들어보세요 :)\n\n아름다운 월정리 바다와\n어촌마을의 정취를 느끼며,\n포비네오두막 게스트하우스에서\n즐겁고 행복한 시간을 보내고\n가셨으면 좋겠습니다~\n\n감사합니다!', '제주', '제주시', '행원로4길 26', 33.5515135, 126.8059763, '자가용 이용시 : 행원로4길 26, 또는 제주시 구좌읍 행원리 1261-1 을 네비에 입력하세요~!!\n\n버스 이용시 : 201번 (동일주버스) 를 이용하시고, 행원리정류장, 또는 금산목정류장 에 하차하세요~!!\n\n픽업서비스 : 구좌읍 내의 어느곳이든 픽업해 드립니다\n(상황에 따라 불가 할수도 있어요)\n픽업시간 : 입실픽업(16:00-18:00), 퇴실픽업(10:00-11:00)', '주차가능\n무료', '0507-1406-8636', null, 47, 'https://map.naver.com/p/entry/place/35394835?c=15.00,0,0,0,dh&placePath=/room?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181258&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&fromPanelNum=1&additionalHeight=76&timestamp=202512181258&locale=ko&svcName=map_pcv5&businessCategory=guesthouse&from=map', 'NAVER', 'Instagram: https://www.instagram.com/2weeks_/', '16:00', '11:00', '따뜻한 분위기와 편안한 환경에서의 휴식');
