INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (131, '제주 레몬트리 게스트하우스 1호점', 'GUESTHOUSE', '아침조식을 뷔페식(매일 10첩반상 이상)으로 무료제공하는게스트하우스 입니다\n\n공항버스 도보로 1분거리 , 일주버스 내륙버스 정류장 도보로 1분이내거리, 모슬포항 도보 5분거리이며 모슬포 맛집 집중거리 특히 1호점은 바닷가 전망이 보이는 역사깊은 게스트하우스입니다\n\n레몬트리는 바닷가 바로앞이며 제주 전역으로 가는 버스터미널 정류장이 도보 1분거리에 바로 있습니다\n\n올레코스 8,9,10,11,12,13,14,14-1코스시작점 끝점까지 버스로 20분이내 이동가능하시며 연박을 하실경우 올레길을 더욱 편하게 걸으실수 있습니다\n보통 4박5일 일정으로 예약하셔도 절대 후회 안하십니다\n가방 매고 힘들게 안다니셔도 됩니당~~\n\n**숙박시 마라도 10% , 가파도 17% 할인 적용되며 산방산 탄산온천 할인 30% 도 됩니다**\n\n*세탁기, 건조기 사용 가능하시며 세탁은 2천원 /건조는 3천원으로 유료이용가능하십니다 (세제무료)\n\n*전 침구류 매일 세탁 /전 객실 소독 방역으로 코로나 예방에 최선을 다하고 있습니다\n\n- 각 객실에 전기 모기채, 에프킬라 준비되어 있습니다\n\n*샴푸,린스,비누,치약,수건 준비되어 있으며 드라이기 고대기 도 있습니다\n\n*자전거 실내 보관이 가능하시며 체크인 전/후 짐을 맡기실 수 있습니다\n\n게스트하우스 근처는 여성 분들이 가장 안전하게 다니실 수 있는 지역입니다 근처에 병원 편의점 은행 시장 오일장 맛집 거리가 도보 2분 이내입니다 바다와 가장 가까우면서 편리성까지 겸비한 진정한 쉼터입니다', '제주', '서귀포시', '하모항구로 70', 33.2196526, 126.2513626, '방어축제거리안에 있습니다', '주차가능\n무료\n건물 왼편 무료주차30대 가능 건물앞무료주차', '0507-1366-2525', null, 658, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EB%A0%88%EB%AA%AC%ED%8A%B8%EB%A6%AC+%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4+1%ED%98%B8%EC%A0%90/place/31846282?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/room?fromPanelNum=1&additionalHeight=76&timestamp=202512181303&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%A0%88%EB%AA%AC%ED%8A%B8%EB%A6%AC%2B%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4%2B1%ED%98%B8%EC%A0%90&businessCategory=guesthouse&additionalHeight=76&timestamp=202512181303&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%A0%88%EB%AA%AC%ED%8A%B8%EB%A6%AC%2B%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4%2B1%ED%98%B8%EC%A0%90&businessCategory=guesthouse&fromPanelNum=1', 'NAVER', 'Blog: https://blog.naver.com/lemontreemom', '16:00', '09:30', '맛있는 조식과 아름다운 뷰의 만남');
