INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (134, '제주 농땡이연구소', 'GUESTHOUSE', '제주공항 근처, 동문시장 바로 앞에 위치한 게스트하우스 입니다.\n\n독립된 공간처럼 활용할 수 있는 침대와, 여독을 풀면서 휴식할 수 있는 매트리스, 편안한 카페 느낌의 라운지에서 여러분만의 여행을 만들고 가세요. 저희가 힘껏 도와드리겠습니다.', '제주', '제주시', '8', 33.5139346, 126.5269377, '동문시장 다이소 맞은편, 버스정류장 뒤 이삭토스트 골목 안 30m 지점\n\n:: 공항 연결 버스 ::\n\n기억하세요, 1-2-3.\n1층 2번출입구 앞 3번 승강장(용담, 시청방면) : 약 10분 소요\n1. 316, 325, 43-2 (동문시장 동문로터리), 465(탐라광장) 정류장 하차\n2. 315(탐라광장), 332(동문시장 동문로터리) 하차\n * 위 노선보다 5~7분정도 더 소요됨\n3. 302, 365, 370 탑승 후 관덕정/동문시장 제민신협 하차후 도보 5분', '주차가능\n무료', NULL, null, 994, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EB%86%8D%EB%95%A1%EC%9D%B4%EC%97%B0%EA%B5%AC%EC%86%8C/place/1489398807?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/room?fromPanelNum=1&additionalHeight=76&timestamp=202512181304&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%86%8D%EB%95%A1%EC%9D%B4%EC%97%B0%EA%B5%AC%EC%86%8C&businessCategory=guesthouse&additionalHeight=76&timestamp=202512181304&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%86%8D%EB%95%A1%EC%9D%B4%EC%97%B0%EA%B5%AC%EC%86%8C&businessCategory=guesthouse&fromPanelNum=1', 'NAVER', 'Instagram: http://www.instagram.com/nondaylab_jeju\nBlog: https://blog.naver.com/nondaylab', '16:00', '11:00', '행정안전부 선정\n착한가격업소');
