INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (136, '제주 아림', 'GUESTHOUSE', '저희는 고객님의 안락과 행복을 위하여 선한 꿈을 안고 \n아름다운 향기가 흐르는 펜션을 운영하고 있습니다. \n고객님 취향에 맞추어 잘 섬겨드리는 아림펜션이 되고자 합니다. \n저희 펜션은 여러분을 감미로운 공간으로 맞이하도록 \n지원하는 5차원의 공간입니다.', '제주', '제주시', '456-8', 33.4889749, 126.4320585, '공항입구에서 용문로를 따라 2.05km 이동 먹돌새기에서 용두암 방면으로 좌회전 후 용해로를 따라 1.51km 지나 한림, 도두 방면으로 좌회전 후 서해안로를 따라 1.37km 이동 좌회전 서해안로를 따라 45m 이동 후 좌회전 46m 이동 하면 아림펜션이 있습니다.', NULL, '064-712-6969', null, 4, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EC%95%84%EB%A6%BC/place/37642064?c=15.00,0,0,0,dh&placePath=/room?entry=bmp&fromPanelNum=2&timestamp=202512181305&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EC%95%84%EB%A6%BC&businessCategory=pension&fromPanelNum=2&timestamp=202512181305&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EC%95%84%EB%A6%BC&businessCategory=pension&from=map', 'NAVER', NULL, '14:00', '11:00', NULL);
