INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (141, '제주 무룸', 'GUESTHOUSE', '안녕하세요\n제주도 서귀포시 성산읍 신산리에 위치한 무룸입니다. @mooroom_stay (무룸 인스타그램)\n조용한 마을에 위치하고 있는 무룸은 걱정 없는 방이라는 뜻입니다.\n아무 생각 없이 자거나 쉬기에도 평화롭고 좋으며\n표선해수욕장 섭지코지 성산일출봉이 가깝습니다.\n도보 5분 내 해안가 산책을 하실 수 있으며 제주 올레길 3코스(B)가 나옵니다.\n\n무룸은 140평 규모로 방 3개와 거실 주방 화장실 앞마당 뒷마당으로 구성되어 있습니다.\n앞마당 귤 밭 옆 주차장에 주차 2대 가능합니다.\n\n구비품목\n캠핑의자 캠핑테이블 캠핑랜턴 캠핑용품 거치대\n다도상 다도세트 스탠바이미 소파베드 마샬 블루투스스피커 와이파이 인터넷 세탑박스 포트넘앤메이슨 차 2종류 일리캡슐커피 2종류 청귤차 꽃차 웰컴스낵 정수기(싱크대 옆에 있습니다.) 냉장고 전자레인지 식기류 인덕션 기본조리도구 일리캡슐커피머신 와인잔 오프너 전기포트\n샴푸 트리트먼트 바디워시 바디로션 핸드워시 헤어에센스 드라이기 온수', '제주', '서귀포시', '신산중앙로 5', 33.3809029, 126.8770269, '신산초등학교 앞 성산봄죽칼국수와 신산리 복지회관 사이 골목 100m', '주차가능', NULL, null, 4, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EB%AC%B4%EB%A3%B8/place/1885229174?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/room?fromPanelNum=1&additionalHeight=76&timestamp=202512181307&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%AC%B4%EB%A3%B8&businessCategory=guesthouse&additionalHeight=76&timestamp=202512181307&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%AC%B4%EB%A3%B8&businessCategory=guesthouse&fromPanelNum=1', 'NAVER', 'Instagram: https://www.instagram.com/mooroom_stay', NULL, NULL, NULL);
