INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (144, '제주 보름', 'GUESTHOUSE', '서귀포 중문에서 제주산 흑돼지를 제대로\n드실 수 있는 곳, 오직 보름숲입니다.\n\n제주 중문 맛집 보름숲은 손님분들께\n최고의 고기를 대접해드리기 위해 당일\n생산된 최상의 흑돼지 신선육을 그대로\n받아 숙성고에서 보름동안 숙성합니다.\n\n시그니처 메뉴인 망우리불 훈증구이는\n숯불에 올린 고기를 짚멍석으로 덮고\n열을 가두어 속까지 익히는 전통 방식으로\n훈연의 향이 스며들어 어디서도 맛보지\n못한 특별한 맛을 선사합니다.\n\n제주 종갓집 한식 명인의 솜씨로 개발하고\n정성껏 만들어낸 밑반찬과 상차림도\n준비되어 있습니다.\n\n방풍림으로 둘러쌓인 안뜰의 공간은 외부\n환경과 차단되어 아늑하고 특별한 공간을\n제공하고, 감각적인 인테리어로 가장\n제주다운 공간이며 고기집 중 가장 아름\n다운 곳이라 해도 과언이 아닙니다.\n\n고즈넉한 넓은 야외좌석도 완비되어 있어\n낮에는 푸른 숲 속에서, 저녁엔 제주의\n아름다운 노을 가운데서 소중한 사람들과\n좋은 분위기를 즐기실 수 있습니다.\n\n중문관광단지와도 가까워 서귀포 중문\n여행코스 중 방문하기도 편리하고 식사의\n처음부터 끝까지 직원들이 케어해 소중한\n시간이 될 수 있도록 하고 있습니다.\n\n가장 맛있는 제주 흑돼지를 먹을 수 있는\n이곳은 매일이 보름, 날마다 축제,\n오늘의 낙원입니다.', '제주', '서귀포시', '255', 33.2229607, 126.2745085, '- 오시는길 안내\n\n[자차 이용 시]\n1100로 255 네비게이션 검색 후 오시면 됩니다.\n\n중문관광단지 인근에 위치해 있으며\n회수사거리에서 500m정도 올라오시면\n왼쪽에 위치하고 있습니다.\n\n[대중교통 이용 시]\n240번 버스 물꾸럭동산 입구 하차 후\n북쪽으로 100m 정도 올라오시면 됩니다.\n\n숲이 감춰둔 너른 뜰 하나\n가장 제주다운 음식과 공간\n밖에서 보면 우거진 숲\n들어와 보면 너른 뜰\n\n굽는 방식부터 곁들이는 찬까지\n제주의 맛으로 가득한 보름숲입니다.', '주차가능\n무료\n매장으로 들어오시면 40여대 주차 가능합니다.', '0507-1484-1041', null, 2200, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EB%B3%B4%EB%A6%84/place/1986476885?c=15.00,0,0,0,dh&placePath=/information?entry=bmp&fromPanelNum=2&timestamp=202512181309&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%B3%B4%EB%A6%84&from=map&fromPanelNum=2&timestamp=202512181309&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC+%EB%B3%B4%EB%A6%84', 'NAVER', 'Instagram: https://www.instagram.com/_boreumsup/', NULL, NULL, '함흥냉면과 어우러진 구수한 고기 맛');
