INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (145, '제주 아소낭', 'GUESTHOUSE', '" \n아소낭이란\n \n예쁘고 높은 소나무가 많은 우리 펜션을 위해\n \n한자인... 娥 예쁠:""아"" / 峨 높을:""아"" 와\n \n소나무의 제주도 사투리 ""소낭""을\n \n합하여 ""아소낭""이라 지어줬답니다~\n \n \n \n우리 아소낭도\n \n소나무처럼 사시사철 한결같은 마음으로\n \n여러분들이 몸과 마음의 여유를 가지며 편안히 쉬다 가실 수 있도록\n \n최선을 다하겠습니다^^"', '제주', '제주시', '명월성로 297-1', null, null, '제주국제공항을 나오셔서 신제주 입구에서 중문, 한림방면으로 우회전 하신 후 도령로를 따라 약 21km이동하시고 봉성교차로에서 봉성방면으로 우회전 한 후 월각로를 따라 3km 직진하셔서 누운오름사거리에서 동명방면으로 좌회전 하세요. 동명교차로에서 명월방면으로 좌회전 하시고 명월대에서 찔레꽃 남쪽마을이 있는 방향으로 우회전 하시고 쭉 오시다가 명월성로로 좌회전 하시어 나오시면 300미터 방면에 아소낭이 있습니다.', NULL, '064-796-8200', null, 2, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EC%95%84%EC%86%8C%EB%82%AD/place/32874640?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/room?fromPanelNum=1&additionalHeight=76&timestamp=202512181309&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EC%95%84%EC%86%8C%EB%82%AD&businessCategory=pension&additionalHeight=76&timestamp=202512181309&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EC%95%84%EC%86%8C%EB%82%AD&businessCategory=pension&fromPanelNum=1', 'NAVER', NULL, '14:00', '11:00', NULL);
