INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (146, '제주 연북정연가', 'GUESTHOUSE', '제주도 조천읍 조천리 조천포구에 위치한 프라이빗 펜션입니다.\n40평형의 총 3층으로 이루어져 있으며,\n바다와 맞닿은 테라피가든에 30여종의 미니허브동산,테라피자쿠지 즐기며 힐링하세요.\n\n1층은 룸(퀸베드)과 주방, 식당, 거실, 욕실이 있으며,\n2층은 휴게시설과 욕실 그리고 원목의자가 비치된 옥상은 썬쎗플레이스\n3층은 하늘의 별을 볼수 있는 다락방(퀸베드)으로 구성되어 있습니다.\n\n바다와 맞닿은 테라피가든 자쿠지에서 티타임을 즐길 수 있는테이블이 준비되어 있습니다.\n연북정연가는 조천포구와 올레18코스와 맞닿아 있으며,특히 조천주변 23곳의 용천수탐방길이 있어 옛 제주현지인의 삶과 문화를 접하며 산책하기에 좋답니다.\n인근에는 삼양검은모레해수욕장,함덕해수욕장,신흥해수욕장,남녀노소 낚시즐기며 회도 먹을수있는 낚고락고 낚시체험공원,북촌구멍난돌,절물휴양림, 에코랜드,서프라이즈테마파크,선녀와나뭇꾼,제주돌문화공원,교래자연휴양림,산굼부리오름,샤려니숲길,비자림..등등 수많은 관광지가 밀집되어있어 다양한 체험과 제주여행 즐기기 너무너무 좋은 곳에 위치해 있답니다.', '제주', '제주시', '조천북1길 11-1', 33.5412717, 126.6360757, '조천읍 조천리 연북정 건너편 조천포구에 위치하고 있으며,\n안내해 드리는 포구(주차장주소)에 주차 후,\n골목으로 진입하시면 연북정연가를 만날수 있습니다.', NULL, NULL, null, 122, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EC%97%B0%EB%B6%81%EC%A0%95%EC%97%B0%EA%B0%80/place/1546981662?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/room?fromPanelNum=1&additionalHeight=76&timestamp=202512181309&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EC%97%B0%EB%B6%81%EC%A0%95%EC%97%B0%EA%B0%80&businessCategory=pension&additionalHeight=76&timestamp=202512181309&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EC%97%B0%EB%B6%81%EC%A0%95%EC%97%B0%EA%B0%80&businessCategory=pension&fromPanelNum=1', 'NAVER', 'Instagram: https://www.instagram.com/jeju_private_pansion\nBlog: https://blog.naver.com/tlsdud2441', '16:00', '11:00', '아름다운 바다와 함께하는 바다 테라피가든');
