INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (162, '제주 월정가옥', 'GUESTHOUSE', '제주독채펜션 월정가옥은 오래된 가옥을 리모델링한 제주 전통 가옥 입니다.\n내외부에 전통적인 제주도 집 틀들이 존재해 정말 옛날의 제주로 온 느낌을 주고요.\n위치 또한 제주공항에서 동쪽으로 20킬로미터 떨어진 구좌읍에 속해 있는 월정리마을에 위치해 있습니다.\n예로부터 주민들은 무주에 또는 무주개라 불렀다고 하네요. ''무주''의 뜻은 확실하지 않으나 ''개''는 포구를 뜻한답니다.\n월정은 마을의 모양이 반달 같고 바닷가에 접해 있다는 뜻으로 ''달이 뜨는 바닷가'' 라는 의미에서 유래되었다고 전해진답니다.\n옛 시골의 향기를 뿜는 것이 아니라 마을 전체가 70년대 후반의 건축물들인 제주의 전통 돌집들이 모여 운치와 평화가 있습니다.\n우리 월정가옥 근처에는 긴 해안도로가 있는데 그 해안도로를 따라 드라이브를 하면 멋진 바다 풍광을 보고 오시면 월정리 마을 안길에 겹겹이 띠를 두른 병풍같은 돌담들이 마중 나옵니다.\n해녀들이 물질 갈때 걸어갔던 숨비소리길을 따라가다 보면 해녀의 숨소리를 들을 수 있을 것만 같고, 천연기념물 제 384호인 당처물 동굴이 있으며, 이쁜 월정리 카페촌과 맛집들이 옹기종기 모여, 이 곳에서 한가로운 하루를 보내 실 수 있습니다.\n월정리에서 시작하여 해원리(바람이 항상 불어 행원풍력단지),평대리(TV에서도 알려진 맛집들),세화리(넓은 해수욕장), 하도리(환해장성,별방진성,철새도래지), 성산(성산일출봉,섭지코지)까지 해안도로를 달리다 보면 정말 볼 것들 맛볼것들이 넘 많은 환상의 동쪽 코스 입니다.\n\n월정가옥은 월정리해수욕장에서 도보로 1분 거리에 위치해 있습니다.\n\n2인커플룸독채 2개와 4인까지 수용가능한 패밀리룸독채로 3개동이 함께있는 숙소입니다. 3개동 모두 대여가능해 단체로도 많이 찾는곳입니다.아이들과 함께 물놀이후에 도보로 1분거리에있어 더더욱 편한위치에 있습니다.\n마당과 옥탑이 있어 개별 사용이 가능하며\n옥탑에선 월정리바다가 시원하게 내려다 보이는 장점이 있습니다.', '제주', '제주시', '월정중길 39 월정가옥', 33.5585531, 126.7926449, NULL, '주차가능\n무료', NULL, null, 198, 'https://map.naver.com/p/entry/place/1201914461?c=15.00,0,0,0,dh&placePath=/room?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181316&locale=ko&svcName=map_pcv5&businessCategory=pension&fromPanelNum=1&additionalHeight=76&timestamp=202512181316&locale=ko&svcName=map_pcv5&businessCategory=pension&from=map', 'NAVER', NULL, '16:00', '10:00', '아름다운 인테리어와 바다 풍광의 조화');
