INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (164, '제주 라온스테이', 'GUESTHOUSE', '제주 동쪽 성산일출봉 근처의 독채풀빌라 라온스테이는 단독 수영장을 갖춘 프라이빗 독채풀빌라에요.\n\n올레길을 따라 걷다보면 온평포구와 가까워 성산의 아름다운 해안길과 만날 수 있어요.\n\n40평 복층구조로 1층에는 침대방1개와온돌방 1개, 욕실1개,8명이 앉을 수 있는 넓은 식탁,복층의 높은 천장거실,단독수영장과 바베큐테라스와세탁실을 갖추고 있어요.\n2층에는 침대방과, 파우더룸이 있는 욕실,야외테라스로 꾸며져 있어요.\n\n코로나로 지친 일상을 달랠 수 있게 입실전 철저한 방역과 소독으로 준비하고 있습니다\n\n가족..친구..연인..소중한 인연들과의 행복한 여행의 추억만들기를 자연향 가득한 힐링하우스 라온스테이와 함께 하세요~^^', '제주', '서귀포시', '온평서로 28-17', 33.4115881, 126.8973090, '제주도 서귀포시 온평서로 28-17 라온스테이', '주차가능', NULL, null, 17, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EB%9D%BC%EC%98%A8%EC%8A%A4%ED%85%8C%EC%9D%B4/place/1913755550?c=15.00,0,0,0,dh&placePath=/information?entry=bmp&fromPanelNum=2&timestamp=202512181316&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%9D%BC%EC%98%A8%EC%8A%A4%ED%85%8C%EC%9D%B4&businessCategory=guesthouse&from=map&fromPanelNum=2&timestamp=202512181316&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%9D%BC%EC%98%A8%EC%8A%A4%ED%85%8C%EC%9D%B4&businessCategory=guesthouse', 'NAVER', 'Blog: https://blog.naver.com/saintsemi\nInstagram: http://www.instagram.com/atomsemi', NULL, NULL, NULL);
