INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (174, '제주 국제호텔', 'GUESTHOUSE', '제주 시내 중심가에 있는 국제호텔은 도심 속 관광 쇼핑 명소에 쉽게 접근할 수 있는 편리한 위치를 자랑하며 공항에서 차로 5분 정도 소요되어 투숙객 분들의 제주도 여행의 시작과 마무리를 위한 가장 이상적인 숙소입니다.\n\n1급 종합병원인 한라병원과 롯데시티호텔 맞은편에 위치해 있으며 롯데면세점, 신라면세점, 드림타워, 대형마트, 신제주 쇼핑명소인 누웨마루거리 등이 도보 5분 거리에 인접해 있어 편리한 여행 및 쇼핑을 즐길 수 있을 뿐만 아니라\n\n대중교통의 요충지로 애월, 한림, 중문 등 제주도내 주요 관광지로 향하는 모든 노선의 버스가 지나는 제주시 최대 규모의 정류장이 바로 앞에 위치해 있어 도내 유명 관광 명소에 쉽고 편리하게 접근할 수 있습니다.\n\n투숙객 편의를 위해 숙소에서 Wi-Fi (무료/전 객실), 일일 청소 서비스, 익스프레스 체크인/체크아웃, 여행가방 및 자전거 보관 등을 제공하며, 숙소 1층에 코인세탁, 국제택배서비스 등 다양한 시설이 마련되어 있습니다.', '제주', '제주시', '8', 33.4892667, 126.4865416, '제주 한라병원, 롯데시티호텔 맞은편', NULL, '0507-1366-4992', null, 123, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EA%B5%AD%EC%A0%9C%ED%98%B8%ED%85%94/place/16843529?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/information?fromPanelNum=1&additionalHeight=76&timestamp=202512181321&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EA%B5%AD%EC%A0%9C%ED%98%B8%ED%85%94&businessCategory=hotel&fromPanelNum=1&additionalHeight=76&timestamp=202512181321&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EA%B5%AD%EC%A0%9C%ED%98%B8%ED%85%94&businessCategory=hotel', 'NAVER', NULL, '15:00', '11:00', '편리한 교통과 깔끔한 식사의 만남');
