INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (176, '제주 서귀 더 부티크', 'GUESTHOUSE', '서귀포 매일 올레시장 도보 5분거리 및 유명 관광지가 가까운 아랑조을거리에 위치해 있는 신축 레지던스 입니다.\n아랑조을거리 안에는 맛집도 많아서 식당찾기 편리합니다.\n이중섭거리,천지연,새연교,자구리공원,외돌개,서귀포항,칠십리 공원등 유명관광지 도보가능 합니다.\n병의원 및 하나로마트 도보 3-5분\n편의점,빨래방,버스정류장 도보 1분도 안걸려요~\n대형마트 차량 홈플러스는 3분, 이마트 8분정도 입니다.\n자차없어도 대중교통 이용 편리합니다.\n쾌적한 객실환경을 위해 취사는 불가하며 전자렌지 이용은 가능합니다. (25평 큰투룸은 취사가능하며 밥솥만 없습니다)\n조미료 및 세탁세제는 비치되어있지않습니다.\n전객실 금연입니다.\n반려견,묘 동반금지 입니다.', '제주', '서귀포시', '10-1', 33.2489261, 126.5587694, NULL, '주차가능', '0507-1336-0261', null, 23, 'https://map.naver.com/p/entry/place/1789077395?c=15.00,0,0,0,dh&placePath=/information?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181322&locale=ko&svcName=map_pcv5&businessCategory=hotel&from=map&fromPanelNum=1&additionalHeight=76&timestamp=202512181322&locale=ko&svcName=map_pcv5&businessCategory=hotel', 'NAVER', NULL, NULL, NULL, '편리한 대중교통으로 만나는 시장과 관광지');
