INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (183, '제주 바다야펜션', 'GUESTHOUSE', '저희 바다야펜션은 제주공항에서 승용차로 30분 거리에 위치해 있고 제주도의 모든 관광지를 1시간 이내로 이동하실 수 있습니다. 펜션 앞으로는 제주의 에메랄드 빛 푸른바다가 아름다운 함덕해수욕장이 펼쳐져있고 모든 객실을 세심하고 세련되게 꾸미기 위해 최선을 다했습니다. 객실에는 바다가 한눈에 들어오는 넓은 창이 있어 푸른바다와 황금빛 모래사장을 바라볼 수 있으며 저녁노을과 한밤의 바다풍경의 아름다움을 만끽할 수 있을 것입니다.', '제주', '제주시', '조함해안로 478 2,3층', 33.5430493, 126.6640235, '공항입구에서 중문,한림,신제주방면으로 우회전하신 후 공항로를 따라 가시다가 신제주입구에서 성산,시청방면으로 좌회전하세요. 서광로를 따라 5.07km 이동후 국립박물관사거리에서 표선,봉개동 방면으로 우회전 하세요. 번영로를 따라 가시다가 거로사거리에서 성산,삼양 방면으로 좌회전후 삼양검문소삼거리가 나오면 성산,조천방면으로 우회전하시고 일주동로를 따라가세요. 큰백교차로에서 좌회전하세요. 함대로를 가시다가 성산,함덕방면으로 우회전 후 함덕우체국이나오면 좌측으로 꺾으시고 썬샤인호텔을 지나 우측으로꺾으시고 제주비치호텔을 지나시면 바다야펜션이 나옵니다.', '주차가능\n무료', '064-784-0800', null, 280, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EB%B0%94%EB%8B%A4%EC%95%BC%ED%8E%9C%EC%85%98/place/31343437?c=15.00,0,0,0,dh&placePath=/room?entry=bmp&fromPanelNum=2&timestamp=202512181325&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%B0%94%EB%8B%A4%EC%95%BC%ED%8E%9C%EC%85%98&businessCategory=pension&fromPanelNum=2&timestamp=202512181325&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EB%B0%94%EB%8B%A4%EC%95%BC%ED%8E%9C%EC%85%98&businessCategory=pension&from=map', 'NAVER', NULL, '15:00', '11:00', '아름다운 바다 전망을 자랑하는 넓고 깨끗한 방');
