INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (184, '제주 스완지 리조트', 'GUESTHOUSE', '스완지리조트 방문을 진심으로 환영합니다.\n제주공항에서 10분거리에 위치해 교통이 편리하고\n제주의 푸른바다가 지척에 있어 파도소리와 바다내음을\n느낄 수 있습니다.\n해안 도로는 올레길로 지정되어 있으먀,\n원시적인 어로시설인 \n"원담"이라는 돌그물과 야간 개장을 하는 \n이호 해수욕장이 인접해 있어 주간에 관광하고\n야간에는 해수욕을 즐길 수 있어 많은 사람들이 찾는 곳입니다.\n고객 여러분께 아름다운 자연풍경과 더불어\n건강까지 휴양을 위한 최적의 조건을 갖추고 있습니다.\n연인들간의 달콤한 휴식과 가족간의 여유로운 여행을\n느낄 수 있도록 마련된 공간입니다.\n사랑하는 사람들과의 편안한 휴식공간으로 함께하는\n스완지가 되겠습니다.', '제주', '제주시', '84', 33.5030450, 126.4582212, '제주공항 주자창 나와서 좌회전 후, 사거리에서 약 1km 직진합니다. 용두암방면으로 좌회전한 후, 해안도로 입구 삼거리에서 좌회전합니다. 해안도로따라서 이호 해수욕장 가기 전 좌측을 보시면 스완지펜션이 보입니다.', NULL, '064-744-6700', null, 94, 'https://map.naver.com/p/entry/place/32785986?c=15.00,0,0,0,dh&placePath=/room?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181325&locale=ko&svcName=map_pcv5&businessCategory=pension&fromPanelNum=1&additionalHeight=76&timestamp=202512181325&locale=ko&svcName=map_pcv5&businessCategory=pension&from=map', 'NAVER', NULL, '15:00', '11:00', '깨끗한 숙소에서 즐기는 아름다운 뷰');
