INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (188, '제주 스테이 비젠빌리지', 'GUESTHOUSE', '도심을 떠나 차별화된 감성 여행을 원하신다면\n\n제주도 ''비젠빌리지''를 찾아주세요.\n\n비젠빌리지에서 700m 하도해수욕장\n\n윌리의 카메라(아날로그 필름카메라)\n대여.\n필름카메라\n\n판매.\n필름 카메라\n일회용 카메라\n로모 카메라\n필름\n\n제주 하도리 아날로그 사진여행(필름사진)\n다양한 체험 프로그램:\n\n- 제주 하도리 아날로그 사진여행(필름사진)\n\n하도리사진관(Slowstone): 사진 스튜디오 예약제\n\n비어라운드 청음카페\n\n1.이곳에선 모두가 주인공\n모든곳이 사진 스튜디오같은 이곳\n-\n동상이몽 ''한고은 부부''\n글랜스 TV\n원나잇 푸드트립 ''오세득쉐프''\nB1A4 뮤직비디오 촬영\n이달의 소녀 뮤직비디오 촬영\n우아 촬영\n예쁘장한 구오즈(가수 육성재)\n아리랑TV\n-\n각종 방송 & 뮤직비디오에 소개된 비젠빌리지\n세트장 같은 숙소에서 인생사진을!\n\n인스타그램\n- 비어라운드 @bearoundcafe\n- 아날로그샵 & 로프트 마켓 @analog_lifestyleshop\n- 슬로우스톤 (하도리사진관) @slowstonejeju\n\n비젠빌리지\n입실시간은 16:00-20:00 입니다.\n\n비행기의 늦은 도착이 아닌 다른 사유인 경우\n20:00 이후 시간당 만원의 추가요금이 적용됩니다.\n21:00 이후는 입실이 허락되지 않습니다.\n다른 분들이 불편하지 않도록 입실시간을 지켜주시길 바랍니다.\n\n예약변경은 네이버예약을 통해서 부탁드립니다.\n: (예약 취소 네이버예약을 통해서만 진행됩니다. 예약시 환불 규정을 확인 후 예약 부탁드립니다.)', '제주', '제주시', '하도9길 72 비젠빌리지', 33.5115710, 126.8908021, '버스 이용시.\n구좌읍 세화리에서 환승 가능합니다.\n\n택시 이용시.\n오후 들어오시는 경우\n로컬택인 구좌읍 택시를 검색하셔서 연락하시면 2만원 미만의 금액으로 이용 가능하세요.\n\n주소. 제주시 구좌읍 하도리 907번지 입니다.', '주차가능\n무료', NULL, null, 436, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%EC%8A%A4%ED%85%8C%EC%9D%B4+%EB%B9%84%EC%A0%A0%EB%B9%8C%EB%A6%AC%EC%A7%80/place/37779178?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/information?fromPanelNum=1&additionalHeight=76&timestamp=202512181327&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EC%8A%A4%ED%85%8C%EC%9D%B4%2B%EB%B9%84%EC%A0%A0%EB%B9%8C%EB%A6%AC%EC%A7%80&businessCategory=pension&fromPanelNum=1&additionalHeight=76&timestamp=202512181327&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%EC%8A%A4%ED%85%8C%EC%9D%B4%2B%EB%B9%84%EC%A0%A0%EB%B9%8C%EB%A6%AC%EC%A7%80&businessCategory=pension', 'NAVER', 'Blog: https://blog.naver.com/wiesenvillagejeju\nInstagram: http://www.instagram.com/wiesenvillage', '16:00', '11:00', NULL);
