INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (193, '제주 대동호텔', 'GUESTHOUSE', '대동호텔은 1970년 7월에 2층 규모의 ''대동여관''으로 시작하였습니다. 창업자인 주인장 부부의 정성과 노력으로 2001년에 리모델링을 하여 지금의 ''호텔대동''으로 거듭났습니다. 100년 호텔을 꿈꾸며 가족 경영을 하는 저희 호텔은 제주에 오시는 손님 한 분, 한 분을 가족처럼 편안하고 따뜻하게 맞이합니다.\n- 여행 + 예술 + 전통의 이야기가 베어있는 호텔!\n- 올레 18코스 시작점 & 동문시장 위치!\n- 제주공항 차량 10분 & 제주항 5분 위치!\n- 여행작가들이 추천하는 호텔!\n- 더블룸 & 트윈룸 & 온돌룸 객실 보유', '제주', '제주시', '6', 33.5137923, 126.5269913, '동문시장 게이트 4 맞은편 골목. 제일공영주차빌딩 앞.(제주공항에서 차로 10~15분)', NULL, '064-723-2600', null, 82, 'https://map.naver.com/p/entry/place/1546371534?c=15.00,0,0,0,dh&placePath=/information?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181329&locale=ko&svcName=map_pcv5&businessCategory=hotel&from=map&fromPanelNum=1&additionalHeight=76&timestamp=202512181329&locale=ko&svcName=map_pcv5&businessCategory=hotel', 'NAVER', NULL, '15:00', '11:00', '편리한 위치에서 추억 남기는 여행');
