INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (194, '제주 HY 초이호스텔', 'GUESTHOUSE', '1. 무료 주차장 완비\n2. 동급 대비 넓은 객실과 전용 욕실,개별 냉난방 시스템\n3. 객실내 TV, 냉장고, 에어컨, 헤어드라이기 구비\n4. 전구역 CCTV 24시간 안전관리\n5. 공용 주방 시설(전자레인지 및 에어프라이기 구비) 취사가능\n6. 올레시장, 이중섭거리, 천지연폭포, 새연교, 서귀포항, 외돌개 등 도보로 이동 가능한 서귀포 관광 중심지에 위치\n7. 제주올레여행자센터 30m 거리, 제주올레 7코스, 7-1코스 시작점 및 6코스 도착점으로 올레길 여행의 최적화 위치.\n8.연박시 생수는 첫날만 제공됩니다. ( 부족시 8층 정수기 사용)\n9. 연박시 수건은 1층 카운터에 수건을 챙겨가시면 됩니다.\n10. 연박시 일주일에 5만원, 14일이상 7만원 한달 10만원 관리비가 있습니다. 관리비는 현장에서 지불 바랍니다.', '제주', '서귀포시', '11-1', 33.2470636, 126.5583324, '제주공항 5번 게이트에서 공항버스 600번을 약 한시간 정도타고 샛기정공원 정류장에서 내리시면 도보로 2분 거리에 있습니다.\n제주공항에서 181번 또는 182번 타시고 중앙로터리 정류장에 하차하시어 도보로 10정도 오시면 됩니다.\n\n시내버스 이용시 평생학습관(201, 202, 281, 282, 521, 633, 651, 652, 691, 5002 버스) 또는 서문로터리입구 또는 구 중앙파출소(510, 531, 532, 611, 615, 627, 641, 690,880, 5003 버스) 에서 내려서 도보 이동하시면 됩니다.\n\n서귀포 올레시장, 이중섭거리, 천지연폭포, 새연교, 서귀포항, 외돌개 등 유명한 서귀포 관광지가 모두 걸어서 이동 가능한 위치에 있습니다.', '주차가능\n무료\n주차장 부족시 앞 건물 도로변에 주차가능하십니다. 건물 입구에도 두대 주차 가능하십니다', '0507-1376-5570', null, 80, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+HY+%EC%B4%88%EC%9D%B4%ED%98%B8%EC%8A%A4%ED%85%94/place/1063598607?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/information?fromPanelNum=1&additionalHeight=76&timestamp=202512181330&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2BHY%2B%EC%B4%88%EC%9D%B4%ED%98%B8%EC%8A%A4%ED%85%94&businessCategory=hotel&fromPanelNum=1&additionalHeight=76&timestamp=202512181330&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2BHY%2B%EC%B4%88%EC%9D%B4%ED%98%B8%EC%8A%A4%ED%85%94&businessCategory=hotel', 'NAVER', NULL, '15:00', '11:00', NULL);
