INSERT INTO room (accommodations_id, room_name, price, min_guests, max_guests, room_description, main_image_url, bathroom_count, room_type, bed_count) VALUES (195, '[독채] 남운재', 250000, 2, 4, '기준 2인 (최대 4인)', 'https://search.pstatic.net/common/?autoRotate=true&quality=95&type=w750&src=https%3A%2F%2Fnaverbooking-phinf.pstatic.net%2F20170912_200%2F15052020298095OwBu_JPEG%2FDSC_3568.jpg', 0, 'STANDARD', 0);
INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (196, '제주 스위스마을 펜션 113동', 'GUESTHOUSE', '최근핫플레이스로 떠오르고있는 제주 조천 스위스마을.TV동물농장에 소개된 바로 그곳입니다.\n독립된 공간의 호텔식 객실이며 룸 안에 욕실을 갖추고 있습니다.\n셀프 체크인 셀프 체크아웃 가능합니다.\n제주 삼다수가 생산되는 물 맑고 공기좋은 중산간 마을입니다.\n마을 내 광장에 서면 멀리 함덕해수욕장과 서우봉이 보이고, 그 옆으로 이어지는 김녕해변의 풍차가 멋지게 펼쳐져 있는것을 감상하실수있습니다.\n\n숙소 : 마을에 편의점과 다양한 점포들이 있어서 커플,나홀로 여행족,가족 등 누구나 이용하기에 적합합니다.\n반경 10km안에 함덕해수욕장,사려니숲길 (생물권보전지역) ,거문오름(세계자연유산),절물자연휴양림,제주돌문화공원,교래자연휴양림,삼양해수욕장,노루생태관찰원,한라생태숲,제주미니랜드,다희연,김녕성세기해변,김녕월정지질트레일,돌하르방공원,에코랜드테마파크,선녀와나무꾼테마공원 등 관광지와 한화리조트, 골프장(크라운cc,제피로스cc,세인트포cc,에코랜드cc)과 인접해 제주여행을 즐기기에 최적화된 숙소입니다.깨끗하고 안전하고 편안한 제주 여행을 즐길수있습니다\n렌터카이용시 ''와산리경로당''을 입력하세요. 스위스마을을 입력하면 산속길을 안내해주니 반드시 와산리경로당을 입력하세요. 와산리경로당 북쪽방향으로 100m왼쪽(함덕해수욕장방향)에 스위스마을 이정표가 있습니다.\n반대방향(함덕해수욕장->와산사거리)으로 오신다면 달빛빌리지를 150m 쯤 지나서 오른쪽에 스위스마을 이정표를 확인할 수 있습니다. 어두워지면 이정표를 못 볼 수도 있으니 서행하세요.', '제주', '제주시', '함와로 566-27', null, null, '제주스위스마을 113동 (와산리 경로당 검색하고 오세요)', NULL, '064-744-6060', null, 40, 'https://map.naver.com/p/entry/place/37696002?c=15.00,0,0,0,dh&placePath=/room?entry=bmp&fromPanelNum=1&additionalHeight=76&timestamp=202512181331&locale=ko&svcName=map_pcv5&businessCategory=pension&fromPanelNum=1&additionalHeight=76&timestamp=202512181331&locale=ko&svcName=map_pcv5&businessCategory=pension&from=map', 'NAVER', 'Blog: https://blog.naver.com/haengbok6060', NULL, NULL, NULL);
