INSERT INTO accommodation (accommodations_id, accommodations_name, accommodations_category, accommodations_description, city, district, address_detail, latitude, longitude, transport_info, parking_info, phone, rating, review_count, source_url, source_platform, sns, check_in_time, check_out_time, short_description) VALUES (209, '제주 하랑 게스트하우스', 'GUESTHOUSE', '2022년 한국소비자산업평가 제주도 서귀포 게스트하우스 1위!\n\n하랑하우스는 안전하고 조용한 1인 전용 게스트하우스로 안심하고 편안히 쉴 수 있도록 배려하였습니다\n\n제주 핵심도로 중 하나인 서일주도로에 바로 근접해 있으며 근처 모슬포항,송악산,산방산,오설록등 유명한 관광지 및 올레 10,11코스와도 가깝습니다.\n또한 최근 유명해진 돌고래 포인트와도 매우 가깝습니다\n\n또한 6개의 최신식 룸시설이 갖추어져 있으며 각 룸 별로 개별 화장실이 갖춰져 있으므로 개별 여행객 및 가족 단위 모두가 편안하게 쉬실 수 있습니다.\n게스트 전용 카페를 운영하고 있으며 2층 카페 테라스에서는 바다를 바라보며 커피 한잔을 즐기실 수 있는 여유를 누리시기 바랍니다\n단기 및 장기 예약 가능합니다(별도문의)\n\n하랑게스트하우스는 핵심 관광지와도 가까운 거리에 위치하고 있습니다\n제주 환상 자전거 도로길과 바로 인접\n\n대정읍 돌고래 포인트 : 차량 5~8분 내외\n모슬포항 : 도보 15분,차량 5분\n올레길 11코스 도보 7~10분, 10코스 10~15분,차량 각 5분\n산방산,송악산 : 차량 10분 내외\n오설록/카멜리아힐/제주도립곶자왈 : 차량 15분 내외\n저지오름/저지리 문화예술마을/현대미술관 : 차량 15분 내외\n협재해수욕장 : 차량 25분 내외\n중문관광단지 : 차량 20분 내외\n\n*돌고래 유료투어 (별도문의)', '제주', '서귀포시', '일과로13번길 1', 33.2327905, 126.2436957, '일과1리 경로회관 맞은편', '주차가능', '0507-1400-5585', null, 310, 'https://map.naver.com/p/search/%EC%A0%9C%EC%A3%BC+%ED%95%98%EB%9E%91+%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4/place/35142645?isCorrectAnswer=true&c=15.00,0,0,0,dh&placePath=/room?fromPanelNum=1&additionalHeight=76&timestamp=202512181336&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%ED%95%98%EB%9E%91%2B%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4&businessCategory=guesthouse&additionalHeight=76&timestamp=202512181336&locale=ko&svcName=map_pcv5&searchText=%EC%A0%9C%EC%A3%BC%2B%ED%95%98%EB%9E%91%2B%EA%B2%8C%EC%8A%A4%ED%8A%B8%ED%95%98%EC%9A%B0%EC%8A%A4&businessCategory=guesthouse&fromPanelNum=1', 'NAVER', 'Instagram: https://www.instagram.com/jeju_haranghouse\nBlog: https://blog.naver.com/jejuharanghouse', '16:00', '10:00', '편안한 침대와 조용한 깨끗한 숙소');
